import React, { useState, useEffect } from 'react';
import { Link, Outlet, useNavigate, useLocation } from 'react-router-dom';
import './Layout.css';

const Layout = () => {
  const [user, setUser] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸÜ localStorage
    const userStr = localStorage.getItem('user');
    const token = localStorage.getItem('token');
    
    if (!token || !userStr) {
      navigate('/login');
      return;
    }
    
    try {
      const userData = JSON.parse(userStr);
      setUser(userData);
    } catch (error) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    // ŸÖÿ≥ÿ≠ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖŸÜ localStorage
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    
    // ÿßŸÑÿßŸÜÿ™ŸÇÿßŸÑ ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
    navigate('/login');
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  if (!user) {
    return (
      <div className="loading-layout">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...</p>
      </div>
    );
  }

  const getRoleName = (role) => {
    switch(role) {
      case 'admin': return 'ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ';
      case 'branch_manager': return 'ŸÖÿØŸäÿ± ŸÅÿ±ÿπ';
      case 'employee': return 'ŸÖŸàÿ∏ŸÅ';
      default: return 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ';
    }
  };

  return (
    <div className="layout">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <div className="logo">
            <span className="logo-text">L3BTY</span>
          </div>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            {sidebarOpen ? '‚óÄ' : '‚ñ∂'}
          </button>
        </div>
        
        {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ */}
        <div className="user-info">
          <div className="user-avatar">
            {user.name?.charAt(0) || 'U'}
          </div>
          <div className="user-details">
            <h4>{user.name || 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ'}</h4>
            <p>{getRoleName(user.role)}</p>
          </div>
        </div>
        
        {/* ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¨ÿßŸÜÿ®Ÿäÿ© */}
        <nav className="sidebar-nav">
          <ul>
            {/* ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ */}
            <li>
              <Link 
                to={user.role === 'admin' ? '/admin/dashboard' : '/employee/dashboard'}
                className={location.pathname.includes('dashboard') ? 'active' : ''}
              >
                <span>üè†</span>
                ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
              </Link>
            </li>
            
            {/* ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ (ŸÑŸÑŸÖÿØŸäÿ± ŸÅŸÇÿ∑) */}
            {user.role === 'admin' && (
              <>
                <li>
                  <Link 
                    to="/admin/users"
                    className={location.pathname === '/admin/users' ? 'active' : ''}
                  >
                    <span>üë•</span>
                    ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
                  </Link>
                </li>
                <li>
                  <Link 
                    to="/admin/games"
                    className={location.pathname === '/admin/games' ? 'active' : ''}
                  >
                    <span>üéÆ</span>
                    ÿßŸÑÿ£ŸÑÿπÿßÿ®
                  </Link>
                </li>
                <li>
                  <Link 
                    to="/admin/settings"
                    className={location.pathname === '/admin/settings' ? 'active' : ''}
                  >
                    <span>‚öôÔ∏è</span>
                    ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
                  </Link>
                </li>
              </>
            )}
            
            {/* ÿßŸÑÿπŸÖŸÑŸäÿßÿ™ ÿßŸÑŸäŸàŸÖŸäÿ© */}
            {(user.role === 'admin' || user.role === 'branch_manager' || user.role === 'employee') && (
              <li>
                <Link 
                  to="/employee/rentals"
                  className={location.pathname === '/employee/rentals' ? 'active' : ''}
                >
                  <span>‚è±Ô∏è</span>
                  ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
                </Link>
              </li>
            )}
          </ul>
          
          {/* ÿßŸÑÿÆÿ±Ÿàÿ¨ */}
          <div className="logout-section">
            <button onClick={handleLogout} className="logout-btn">
              <span>üö™</span>
              ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
            </button>
          </div>
        </nav>
      </aside>

      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="main-content">
        {/* Header */}
        <header className="main-header">
          <div className="header-left">
            <h1>ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© L3BTY Store</h1>
          </div>
          <div className="header-right">
            <span className="user-welcome">ŸÖÿ±ÿ≠ÿ®ÿßŸãÿå {user.name}</span>
            <span className="user-role-badge">{getRoleName(user.role)}</span>
          </div>
        </header>
        
        {/* ŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿµŸÅÿ≠ÿ© */}
        <div className="content-area">
          <Outlet />
        </div>
        
        {/* Footer */}
        <footer className="main-footer">
          <p>¬© 2024 L3BTY Store - ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
        </footer>
      </main>
    </div>
  );
};

export default Layout;
