import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminPages.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0,
    branchRevenues: [],
    completedRentalsByBranch: []
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);
  const [timeAgo, setTimeAgo] = useState('Ù‚Ø¨Ù„ Ù‚Ù„ÙŠÙ„');

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª ÙƒÙ„ Ø«Ø§Ù†ÙŠØ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
      updateTimeAgo();
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†Ù‚Ø¶ÙŠ
    const timeAgoInterval = setInterval(updateTimeAgo, 60000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
    const dataInterval = setInterval(loadDashboardData, 60000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(timeAgoInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const updateTimeAgo = () => {
    const now = new Date();
    const minutes = Math.floor((now - currentTime) / 60000);
    
    if (minutes === 0) {
      setTimeAgo('Ø§Ù„Ø¢Ù†');
    } else if (minutes === 1) {
      setTimeAgo('Ù‚Ø¨Ù„ Ø¯Ù‚ÙŠÙ‚Ø©');
    } else if (minutes < 60) {
      setTimeAgo(`Ù‚Ø¨Ù„ ${minutes} Ø¯Ù‚ÙŠÙ‚Ø©`);
    } else {
      const hours = Math.floor(minutes / 60);
      setTimeAgo(`Ù‚Ø¨Ù„ ${hours} Ø³Ø§Ø¹Ø©`);
    }
  };

  // Ø¬Ù„Ø¨ Ø¨ÙŠØ§Ù†Ø§Øª Ø­Ù‚ÙŠÙ‚ÙŠØ© Ù…Ù† Ù‚Ø§Ø¹Ø¯Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  const loadRealBranchesData = async () => {
    try {
      const today = new Date().toISOString().split('T')[0];
      const branchRevenues = [];
      const completedRentalsByBranch = [];
      
      // Ø¬Ù„Ø¨ Ø§Ù„ÙØ±ÙˆØ¹ Ø§Ù„Ø­Ù‚ÙŠÙ‚ÙŠØ©
      const branchesResponse = await api.getBranches();
      if (branchesResponse.success) {
        const branchesData = branchesResponse.data || [];
        setBranches(branchesData);
        
        // Ø¬Ù„Ø¨ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù„ÙƒÙ„ ÙØ±Ø¹
        for (const branch of branchesData) {
          try {
