},

  // ================= UTILITY FUNCTIONS =================
  formatError: (error) => {
    if (error.response) {
      return error.response.data?.message || `ÿÆÿ∑ÿ£ ${error.response.status}: ${error.response.statusText}`;
    } else if (error.request) {
      return 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ';
    } else {
      return error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
  },

  hasRole: (role) => {
    const user = authService.getCurrentUser();
    return user?.role === role;
  },

  hasAnyRole: (roles) => {
    const user = authService.getCurrentUser();
    return roles.includes(user?.role);
  },

  validateCustomerPhone: (phone) => {
    const phoneRegex = /^01[0-9]{9}$/;
    return phoneRegex.test(phone);
  },

  formatCurrency: (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(num);
  },

  formatDuration: (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours} ÿ≥ÿßÿπÿ©${remainingMinutes > 0 ? ` ${remainingMinutes} ÿØŸÇŸäŸÇÿ©` : ''}`;
  },

  formatTime: (dateString) => {
    try {
      return new Date(dateString).toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--:--';
    }
  },

  formatDate: (dateString) => {
    try {
      return new Date(dateString).toLocaleDateString('ar-EG', {
        weekday: 'short',
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch {
      return '--/--/----';
    }
  },

  showNotification: (type, message) => {
    const messages = {
      error: `‚ùå ${message}`,
      success: `‚úÖ ${message}`,
      warning: `‚ö†Ô∏è ${message}`,
      info: `‚ÑπÔ∏è ${message}`
    };
    alert(messages[type] || message);
  }
};

export default authService;
