        setBranches([]);
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      const rentalsRes = await api.getRentalsByPermission({ 
        limit: 1000,
        include_game: true
      });
      
      if (rentalsRes.success) {
        setRentals(rentalsRes.data || []);
      } else {
        console.warn('ØªØ­Ø°ÙŠØ±: ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', rentalsRes.message);
        setRentals([]);
        throw new Error(rentalsRes.message || 'ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª');
      }
      
    } catch (error) {
      console.error('Error loading data:', error);
      setError(error.message || 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
      showNotification('error', 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
