// src/services/authService.js - النسخة النظيفة
import api from './api';

const authService = {
  // ================= AUTHENTICATION =================
  
  login: async (email, password) => {
    try {
      const response = await api.login(email, password);
      
      return response.success ? {
        success: true,
        data: response.data,
        message: 'تم تسجيل الدخول بنجاح'
      } : {
        success: false,
        message: response.message || 'فشل تسجيل الدخول'
      };
    } catch (error) {
      return {
        success: false,
        message: 'تعذر الاتصال بالخادم. تأكد من تشغيل الخادم على المنفذ 5000'
      };
    }
  },

  logout: () => {
    api.logout();
  },

  getCurrentUser: () => {
    try {
      const userStr = localStorage.getItem('user');
      return userStr ? JSON.parse(userStr) : null;
    } catch (error) {
      return null;
    }
  },

  getToken: () => {
    const token = localStorage.getItem('token');
    return token ? token.trim() : null;
  },

  isAuthenticated: () => {
    const token = authService.getToken();
    const user = authService.getCurrentUser();
    return !!(token && user);
  },

  // ================= PROFILE MANAGEMENT =================
  
  updateProfile: async (profileData) => {
    try {
      const response = await api.updateProfile(profileData);
      
      if (response.success && response.user) {
        localStorage.setItem('user', JSON.stringify(response.user));
      }
      
      return response;
    } catch (error) {
      return {
        success: false,
        message: error.message || 'تعذر تحديث الملف الشخصي'
      };
    }
  },

  changePassword: async (passwordData) => {
    try {
      return await api.changePassword(passwordData);
    } catch (error) {
      return {
        success: false,
        message: error.message || 'تعذر تغيير كلمة المرور'
      };
    }
  },

  // ================= BRANCHES MANAGEMENT =================
  
  getBranches: async () => {
    try {
      return await api.get('/branches');
    } catch (error) {
      return {
        success: false,
        message: 'تعذر تحميل الفروع',
        data: []
      };
    }
  },

  createBranch: async (branchData) => {
    try {
      return await api.post('/branches', branchData);
    } catch (error) {
      return {
        success: false,
        message: 'تعذر إنشاء الفرع'
      };
    }
  },

  updateBranch: async (id, branchData) => {
    try {
      return await api.put(`/branches/${id}`, branchData);
    } catch (error) {
      return {
        success: false,
        message: 'تعذر تحديث الفرع'
      };
    }
  },

  deleteBranch: async (id) => {
    try {
      return await api.delete(`/branches/${id}`);
    } catch (error) {
      return {
        success: false,
        message: 'تعذر حذف الفرع'
      };
    }
  },

  // ================= GAMES MANAGEMENT =================
  
  getGames: async (params = {}) => {
    try {
      return await api.get('/games', { params });
    } catch (error) {
      return {
        success: false,
        message: 'تعذر تحميل الألعاب',
        data: [],
        stats: { total: 0, available: 0, rented: 0, maintenance: 0 }
      };
    }
  },

