  // ================= GET AVAILABLE GAMES FOR BRANCH =================
  
  getAvailableGamesForBranch: async (branchId) => {
    try {
      const [allGames, branchGames] = await Promise.all([
        authService.getGames(),
        authService.getBranchGames(branchId)
      ]);
      
      if (allGames.success && branchGames.success) {
        const addedGameIds = branchGames.data.map(g => g.game_id || g.id);
        const availableGames = allGames.data.filter(game => !addedGameIds.includes(game.id));
        
        return { 
          success: true, 
          data: availableGames 
        };
      }
      return { success: false, message: 'فشل في جلب الألعاب المتاحة' };
    } catch (error) {
