// frontend/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import MainLayout from './components/MainLayout';

// الصفحات الرئيسية
import Login from './pages/Login';
import AdminDashboard from './pages/admin/Dashboard';
import EmployeeDashboard from './pages/employee/Dashboard';
import AnalyticsPage from './pages/AnalyticsPage';
import ReportsPage from './pages/ReportsPage';
import RentalPage from './pages/RentalPage';

// صفحات المدير
import UsersManagement from './pages/admin/UsersManagement';
import BranchesManagement from './pages/admin/BranchesManagement';
import GamesManagement from './pages/admin/GamesManagement';
import ActivitiesLog from './pages/admin/ActivitiesLog';
import Settings from './pages/admin/Settings';

// صفحات الموظف
import EmployeeRentals from './pages/employee/Rentals';
import EmployeeGames from './pages/employee/Games';

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* صفحة تسجيل الدخول (بدون Layout) */}
          <Route path="/login" element={<Login />} />
          
          {/* المسارات الرئيسية (مع Layout) */}
          <Route path="/" element={<MainLayout />}>
            {/* الصفحة الرئيسية */}
            <Route index element={<Navigate to="/dashboard" replace />} />
            <Route path="dashboard" element={
              <RequireAuth>
                {(user) => user.role === 'employee' ? <EmployeeDashboard /> : <AdminDashboard />}
              </RequireAuth>
            } />
            
            {/* صفحات المدير */}
            <Route path="admin/users" element={
              <RequireAuth allowedRoles={['admin', 'branch_manager']}>
                <UsersManagement />
              </RequireAuth>
            } />
            <Route path="admin/branches" element={
              <RequireAuth allowedRoles={['admin', 'branch_manager']}>
                <BranchesManagement />
              </RequireAuth>
            } />
            <Route path="admin/games" element={
              <RequireAuth allowedRoles={['admin', 'branch_manager']}>
                <GamesManagement />
              </RequireAuth>
            } />
            <Route path="admin/activities" element={
              <RequireAuth allowedRoles={['admin', 'branch_manager']}>
                <ActivitiesLog />
              </RequireAuth>
            } />
            <Route path="admin/settings" element={
              <RequireAuth allowedRoles={['admin']}>
                <Settings />
              </RequireAuth>
            } />
            
            {/* صفحات الموظف */}
            <Route path="employee/rentals" element={
              <RequireAuth allowedRoles={['employee']}>
                <EmployeeRentals />
              </RequireAuth>
            } />
            <Route path="employee/games" element={
              <RequireAuth allowedRoles={['employee']}>
                <EmployeeGames />
              </RequireAuth>
            } />
            <Route path="rental" element={
              <RequireAuth allowedRoles={['employee']}>
                <RentalPage />
              </RequireAuth>
            } />
            
            {/* صفحات عامة */}
            <Route path="analytics" element={
              <RequireAuth allowedRoles={['admin', 'branch_manager']}>
                <AnalyticsPage />
              </RequireAuth>
            } />
            <Route path="reports" element={
              <RequireAuth allowedRoles={['admin', 'branch_manager']}>
                <ReportsPage />
              </RequireAuth>
            } />
          </Route>
          
          {/* كل المسارات الأخرى */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

// مكون للتحقق من التوثيق
const RequireAuth = ({ children, allowedRoles = [] }) => {
  const token = localStorage.getItem('token');
  const userStr = localStorage.getItem('user');
  
  if (!token || !userStr) {
    return <Navigate to="/login" replace />;
  }
  
  try {
    const user = JSON.parse(userStr);
    
    if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
      return <Navigate to="/dashboard" replace />;
    }
    
    // إذا كانت children دالة، نمرر لها user
    if (typeof children === 'function') {
      return children(user);
    }
    
    return children;
  } catch (error) {
    return <Navigate to="/login" replace />;
  }
};

export default App;
