// frontend/src/pages/employee/Rentals.jsx - ÿßŸÑŸÖÿ≠ÿØÿ´
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    completedToday: 0,
    todayRevenue: 0
  });

  useEffect(() => {
    if (user) {
      loadData();
    }
  }, [user]);

  const loadData = async () => {
    try {
      setLoading(true);
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
      if (user?.branch_id) {
        const branchesResponse = await api.get('/branches');
        if (branchesResponse.data.success) {
          const userBranch = branchesResponse.data.data.find(b => b.id === user.branch_id);
          setBranch(userBranch);
        }
      }

      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
      const gamesResponse = await api.get('/games');
      if (gamesResponse.data.success) {
        const available = gamesResponse.data.data.filter(game => game.status === 'ŸÖÿ™ÿßÿ≠');
        setAvailableGames(available);
        setStats(prev => ({ ...prev, availableGames: available.length }));
      }

      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      const rentalsResponse = await api.get('/rentals?status=ŸÜÿ¥ÿ∑');
      if (rentalsResponse.data.success) {
        setActiveRentals(rentalsResponse.data.data);
        setStats(prev => ({ ...prev, activeRentals: rentalsResponse.data.data.length }));
      }

      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸäŸàŸÖ
      const statsResponse = await api.get('/dashboard/stats');
      if (statsResponse.data.success) {
        setStats(prev => ({
          ...prev,
          todayRevenue: statsResponse.data.data.todayRevenue,
          completedToday: statsResponse.data.data.todayRentals
        }));
      }

    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP'
    }).format(amount || 0);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* Header */}
      <div className="page-header">
        <div>
          <h1>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</h1>
          <p>
            {branch ? `ŸÅÿ±ÿπ ${branch.name}` : 'ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®'} | 
            ÿßŸÑŸÖŸàÿ∏ŸÅ: {user?.name || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}
          </p>
        </div>
        <button
          onClick={() => setShowForm(true)}
          className="btn-primary"
        >
          ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ
        </button>
      </div>

      {/* Statistics */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üéÆ</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</h3>
            <h2>{stats.availableGames}</h2>
            <p>ŸÑÿπÿ®ÿ©</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚è±Ô∏è</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h3>
            <h2>{stats.activeRentals}</h2>
            <p>ÿ™ÿ£ÿ¨Ÿäÿ±</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-content">
            <h3>ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿßŸÑŸäŸàŸÖ</h3>
            <h2>{stats.completedToday}</h2>
            <p>ÿ™ÿ£ÿ¨Ÿäÿ±</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üí∞</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖ</h3>
            <h2>{formatCurrency(stats.todayRevenue)}</h2>
            <p>ÿ¨ŸÜŸäŸá ŸÖÿµÿ±Ÿä</p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Available Games */}
        <div className="section">
          <h2>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±</h2>
          
          {availableGames.length === 0 ? (
            <div className="empty-state">
              <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ© ÿ≠ÿßŸÑŸäÿßŸã</p>
            </div>
          ) : (
            <div className="games-grid">
              {availableGames.map(game => (
                <div key={game.id} className="game-card">
                  <div className="game-image">
                    <img 
                      src={`/images/${game.image_url || 'default-game.jpg'}`}
                      alt={game.name}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/drift-car.jpg';
                      }}
                    />
                  </div>
                  <div className="game-info">
                    <h3>{game.name}</h3>
                    <p className="game-category">{game.category}</p>
                    <div className="game-price">
                      <span className="hourly">{formatCurrency(game.price_per_hour)}/ÿ≥ÿßÿπÿ©</span>
                      <span className="quarter">{formatCurrency(game.price_per_15min)}/ÿ±ÿ®ÿπ ÿ≥ÿßÿπÿ©</span>
                    </div>
                    <button className="btn-rent">
                      ÿ™ÿ£ÿ¨Ÿäÿ±
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Active Rentals */}
        <div className="section">
          <h2>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h2>
          
          {activeRentals.length === 0 ? (
            <div className="empty-state">
              <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ© ÿ≠ÿßŸÑŸäÿßŸã</p>
            </div>
          ) : (
            <div className="rentals-list">
              {activeRentals.map(rental => (
                <div key={rental.id} className="rental-item">
                  <div className="rental-header">
                    <h3>#{rental.rental_number}</h3>
                    <span className="rental-status active">ŸÜÿ¥ÿ∑</span>
                  </div>
                  <div className="rental-details">
                    <p><strong>ÿßŸÑÿπŸÖŸäŸÑ:</strong> {rental.customer_name}</p>
                    <p><strong>ÿßŸÑŸÑÿπÿ®ÿ©:</strong> {rental.game_name}</p>
                    <p><strong>ÿ®ÿØÿ£ ŸÅŸä:</strong> {new Date(rental.start_time).toLocaleTimeString('ar-EG')}</p>
                    <p><strong>ÿßŸÑŸÖÿ®ŸÑÿ∫:</strong> {formatCurrency(rental.total_amount)}</p>
                  </div>
                  <div className="rental-actions">
                    <button className="btn-extend">ÿ™ŸÖÿØŸäÿØ</button>
                    <button className="btn-complete">ÿ•ŸÜŸáÿßÿ°</button>
                    <button className="btn-cancel">ÿ•ŸÑÿ∫ÿßÿ°</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Rental Form Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ</h2>
              <button onClick={() => setShowForm(false)} className="close-btn">
                ‚úï
              </button>
            </div>
            <div className="modal-body">
              <p>ŸÜŸÖŸàÿ∞ÿ¨ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ¨ÿØŸäÿØ - ŸÇŸäÿØ ÿßŸÑÿ™ÿ∑ŸàŸäÿ±</p>
              <button 
                onClick={() => setShowForm(false)}
                className="btn-secondary"
              >
                ÿ•ÿ∫ŸÑÿßŸÇ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Rentals;
