import React, { createContext, useState, useContext, useEffect } from 'react';
import authService from '../services/authService';

const AuthContext = createContext({});

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸÜ localStorage ÿπŸÜÿØ ÿßŸÑÿ®ÿØÿ°
  useEffect(() => {
    console.log('üîç AuthContext: ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ...');
    
    const loadUser = () => {
      try {
        const token = authService.getToken();
        const userData = authService.getCurrentUser();
        
        // ‚úÖ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ backticks ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠
        console.log(`üìä ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿµÿßÿØŸÇÿ©: token=${token ? 'ŸÖŸàÿ¨ŸàÿØ' : 'ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ'}, user=${userData ? 'ŸÖŸàÿ¨ŸàÿØ' : 'ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ'}`);
        
        if (token && userData) {
          setUser(userData);
          setIsAuthenticated(true);
          console.log(`‚úÖ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑ: ${userData.name} (${userData.role})`);
        } else {
          console.log('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑ');
          setUser(null);
          setIsAuthenticated(false);
        }
      } catch (error) {
        console.error(`‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ: ${error.message}`);
        setError(error.message);
        setUser(null);
        setIsAuthenticated(false);
      } finally {
        setLoading(false);
      }
    };

    loadUser();
  }, []);

  const login = async (email, password) => {
    try {
      setLoading(true);
      setError(null);
      
      console.log(`üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ: ${email}`);
      
      const response = await authService.login(email, password);
      
      if (response.success) {
        const userData = response.data?.user || authService.getCurrentUser();
        
        setUser(userData);
        setIsAuthenticated(true);
        
        console.log(`‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠: ${userData?.name}`);
        return { success: true, user: userData };
      } else {
        setError(response.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ');
        return { success: false, message: response.message };
      }
    } catch (error) {
      console.error(`üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ: ${error.message}`);
      setError(error.message || 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ');
      return { success: false, message: error.message };
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    console.log('üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨...');
    authService.logout();
    setUser(null);
    setIsAuthenticated(false);
    setError(null);
  };

  const updateUser = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
    console.log(`‚úèÔ∏è ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ: ${userData.name}`);
  };

  const checkPermission = (requiredRole) => {
    if (!user) return false;
    if (user.role === 'admin') return true; // ÿßŸÑŸÖÿØŸäÿ± ŸÑŸá ÿ¨ŸÖŸäÿπ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™
    const hasPermission = user.role === requiredRole;
    console.log(`üîê ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©: ${requiredRole} = ${hasPermission}`);
    return hasPermission;
  };

  const value = {
    user,
    isAuthenticated,
    loading,
    error,
    login,
    logout,
    updateUser,
    checkPermission
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
