import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw, Calculator,
  Play, Pause, StopCircle, AlertTriangle,
  Info, LogOut, Save, Edit, CreditCard, Wallet, Hash, Timer,
  Award, BarChart, TrendingUp, Activity, Gift, Star, Smile,
  Coffee, Sun, Moon, Watch, Hourglass, Coffee as CoffeeIcon
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== دوال المساعدة العامة ====================
const formatCurrency = (amount) => {
  if (amount === null || amount === undefined || amount === '') return '0 ج.م';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--/---- --:--';
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime = new Date()) => {
  if (!startTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  if (isNaN(start.getTime()) || isNaN(end.getTime())) return 0;
  return Math.max(0, Math.round((end - start) / (1000 * 60)));
};

// ==================== مكون شريط حالة الشيفت ====================
const ShiftStatusBar = React.memo(({ currentShift, shiftStats, onStartShift, onEndShift, loading }) => {
  const [showEndShiftConfirm, setShowEndShiftConfirm] = useState(false);
  const [closingCash, setClosingCash] = useState('');
  const [notes, setNotes] = useState('');

  if (loading) {
    return (
      <div className="shift-status-bar loading">
        <Loader2 className="spinner" size={24} />
        <span>جاري تحميل بيانات الشيفت...</span>
      </div>
    );
  }

  if (!currentShift) {
    return (
      <div className="shift-status-bar closed">
        <div className="shift-status-info">
          <Lock size={20} />
          <span>لا يوجد شيفت نشط</span>
        </div>
        <button 
          onClick={onStartShift}
          className="btn btn-success btn-sm"
          disabled={loading}
        >
          <Unlock size={16} />
          فتح شيفت جديد
        </button>
      </div>
    );
  }

  return (
    <>
      <div className="shift-status-bar open">
        <div className="shift-status-info">
          <Zap size={20} className="shift-icon" />
          <div className="shift-details">
            <span className="shift-number">شيفت #{currentShift.shift_number || currentShift.id}</span>
            <span className="shift-time">
              <Clock size={14} />
              {formatDateTime(currentShift.start_time)}
            </span>
          </div>
        </div>

        <div className="shift-stats">
          <div className="shift-stat">
            <DollarSign size={16} />
            <div className="shift-stat-content">
              <span className="shift-stat-label">الإيراد</span>
              <span className="shift-stat-value">{formatCurrency(shiftStats?.total_revenue || 0)}</span>
            </div>
          </div>

          <div className="shift-stat">
            <ShoppingCart size={16} />
            <div className="shift-stat-content">
              <span className="shift-stat-label">التأجيرات</span>
              <span className="shift-stat-value">{shiftStats?.total_rentals || 0}</span>
            </div>
          </div>

          <div className="shift-stat">
            <Activity size={16} />
            <div className="shift-stat-content">
              <span className="shift-stat-label">نشط</span>
              <span className="shift-stat-value">{shiftStats?.active_rentals || 0}</span>
            </div>
          </div>
        </div>

        <button 
          onClick={() => setShowEndShiftConfirm(true)}
          className="btn btn-warning btn-sm"
          disabled={loading}
        >
          <Lock size={16} />
          إنهاء الشيفت
        </button>
      </div>

      {/* نافذة تأكيد إنهاء الشيفت */}
      {showEndShiftConfirm && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <AlertTriangle size={24} />
              <h3>إنهاء الشيفت</h3>
              <button onClick={() => setShowEndShiftConfirm(false)} className="modal-close">
                <X size={20} />
              </button>
            </div>
            
            <div className="modal-body">
              <p>هل أنت متأكد من إنهاء الشيفت الحالي؟</p>
              
              {shiftStats?.active_rentals > 0 && (
                <div className="alert alert-warning">
                  <AlertCircle size={16} />
                  <span>يوجد {shiftStats.active_rentals} تأجير نشط. سيتم إغلاقها تلقائياً.</span>
                </div>
              )}

              <div className="form-group">
                <label>إجمالي الإيراد المتوقع:</label>
                <div className="shift-summary">
                  <div className="summary-row">
                    <span>عدد التأجيرات:</span>
                    <strong>{shiftStats?.total_rentals || 0}</strong>
                  </div>
                  <div className="summary-row total">
                    <span>الإيراد:</span>
                    <strong>{formatCurrency(shiftStats?.total_revenue || 0)}</strong>
                  </div>
                </div>
              </div>

              <div className="form-group">
                <label>خزنة الإغلاق (اختياري):</label>
                <input
                  type="number"
                  value={closingCash}
                  onChange={(e) => setClosingCash(e.target.value)}
                  placeholder="المبلغ الموجود في الخزنة"
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label>ملاحظات:</label>
                <textarea
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                  placeholder="أي ملاحظات عن الشيفت..."
                  rows="3"
                  className="form-control"
                />
              </div>
            </div>

            <div className="modal-footer">
              <button 
                onClick={() => setShowEndShiftConfirm(false)}
                className="btn btn-secondary"
              >
                إلغاء
              </button>
              <button 
                onClick={() => {
                  onEndShift(closingCash, notes);
                  setShowEndShiftConfirm(false);
                }}
                className="btn btn-warning"
                disabled={loading}
              >
                {loading ? <Loader2 className="spinner" size={16} /> : <Lock size={16} />}
                تأكيد إنهاء الشيفت
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
});

// ==================== مكون سلة التأجير ====================
const RentalCart = React.memo(({
  items,
  onUpdateItem,
  onRemoveItem,
  onClearCart,
  customerInfo,
  onCustomerInfoChange,
  onSubmit,
  isSubmitting,
  games,
  onAddGame,
  currentShift
}) => {
  const calculateItemTotal = useCallback((item) => {
    if (item.rental_type === 'open') return 0;
    const units = Math.ceil((item.duration_minutes || 15) / 15);
    return (item.price_per_15min || 0) * units * (item.quantity || 1);
  }, []);

  const calculateCartTotal = useCallback(() => {
    return items.reduce((total, item) => total + calculateItemTotal(item), 0);
  }, [items, calculateItemTotal]);

  const isCartValid = useCallback(() => {
    return items.length > 0 && customerInfo.name?.trim() && currentShift;
  }, [items.length, customerInfo.name, currentShift]);

  if (!currentShift) {
    return (
      <div className="cart-empty">
        <Lock size={48} />
        <h3>الشيفت مغلق</h3>
        <p>يجب فتح شيفت أولاً لبدء التأجير</p>
      </div>
    );
  }

  if (items.length === 0) {
    return (
      <div className="cart-empty">
        <ShoppingCart size={48} />
        <h3>السلة فارغة</h3>
        <p>اختر ألعاباً من القائمة لإضافتها إلى السلة</p>
        <button
          onClick={onAddGame}
          className="btn btn-primary btn-sm"
        >
          <Plus size={16} />
          إضافة لعبة
        </button>
      </div>
    );
  }

  return (
    <div className="cart">
      <div className="cart-header">
        <h3>
          <ShoppingCart size={20} />
          سلة التأجير
          <span className="cart-count">{items.length}</span>
        </h3>
        <button
          onClick={onClearCart}
          className="btn btn-outline btn-sm"
          disabled={isSubmitting}
        >
          <Trash2 size={14} />
          تفريغ
        </button>
      </div>

      <div className="cart-items">
        {items.map((item, index) => (
          <div key={item.id || index} className="cart-item">
            <div className="cart-item-header">
              <div className="cart-item-name">
                <Gamepad2 size={14} />
                <span>{item.game_name}</span>
              </div>
              <button
                onClick={() => onRemoveItem(item.id)}
                className="btn-remove"
                disabled={isSubmitting}
              >
                <X size={14} />
              </button>
            </div>

            <div className="cart-item-details">
              <div className="cart-item-field">
                <label>النوع:</label>
                <select
                  value={item.rental_type}
                  onChange={(e) => onUpdateItem(item.id, { rental_type: e.target.value })}
                  disabled={isSubmitting}
                  className="select-small"
                >
                  <option value="fixed">⏰ ثابت</option>
                  <option value="open">🔓 مفتوح</option>
                </select>
              </div>

              {item.rental_type === 'fixed' && (
                <div className="cart-item-field">
                  <label>المدة:</label>
                  <select
                    value={item.duration_minutes || 15}
                    onChange={(e) => onUpdateItem(item.id, { duration_minutes: parseInt(e.target.value) })}
                    disabled={isSubmitting}
                    className="select-small"
                  >
                    <option value="15">15 دقيقة</option>
                    <option value="30">30 دقيقة</option>
                    <option value="45">45 دقيقة</option>
                    <option value="60">60 دقيقة</option>
                    <option value="90">90 دقيقة</option>
                    <option value="120">120 دقيقة</option>
                  </select>
                </div>
              )}

              <div className="cart-item-field">
                <label>الكمية:</label>
                <div className="quantity-control">
                  <button
                    onClick={() => onUpdateItem(item.id, { quantity: Math.max(1, (item.quantity || 1) - 1) })}
                    className="quantity-btn"
                    disabled={isSubmitting || (item.quantity || 1) <= 1}
                  >
                    <Minus size={12} />
                  </button>
                  <span className="quantity-value">{item.quantity || 1}</span>
                  <button
                    onClick={() => onUpdateItem(item.id, { quantity: (item.quantity || 1) + 1 })}
                    className="quantity-btn"
                    disabled={isSubmitting}
                  >
                    <Plus size={12} />
                  </button>
                </div>
              </div>

              <div className="cart-item-field full-width">
                <label>اسم الطفل:</label>
                <input
                  type="text"
                  value={item.child_name || ''}
                  onChange={(e) => onUpdateItem(item.id, { child_name: e.target.value })}
                  placeholder="اختياري"
                  disabled={isSubmitting}
                  className="input-small"
                />
              </div>
            </div>

            {item.rental_type === 'fixed' && (
              <div className="cart-item-total">
                <span>المجموع:</span>
                <span className="amount">{formatCurrency(calculateItemTotal(item))}</span>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="cart-footer">
        <button
          onClick={onAddGame}
          className="btn btn-outline btn-block"
          disabled={isSubmitting}
        >
          <Plus size={16} />
          إضافة لعبة أخرى
        </button>

        <div className="cart-total">
          <span>الإجمالي:</span>
          <span className="total-amount">{formatCurrency(calculateCartTotal())}</span>
        </div>

        <div className="cart-customer">
          <h4>معلومات العميل</h4>
          <div className="cart-customer-fields">
            <div className="form-group">
              <User size={16} className="field-icon" />
              <input
                type="text"
                placeholder="اسم العميل *"
                value={customerInfo.name || ''}
                onChange={(e) => onCustomerInfoChange('name', e.target.value)}
                disabled={isSubmitting}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <Phone size={16} className="field-icon" />
              <input
                type="tel"
                placeholder="رقم الهاتف"
                value={customerInfo.phone || ''}
                onChange={(e) => onCustomerInfoChange('phone', e.target.value.replace(/\D/g, '').slice(0, 11))}
                disabled={isSubmitting}
                className="form-control"
                maxLength={11}
              />
            </div>
          </div>
        </div>

        <button
          onClick={onSubmit}
          className="btn btn-primary btn-block btn-large"
          disabled={isSubmitting || !isCartValid()}
        >
          {isSubmitting ? (
            <>
              <Loader2 className="spinner" size={18} />
              جاري إنشاء التأجير...
            </>
          ) : (
            <>
              <Check size={18} />
              تأكيد التأجير
            </>
          )}
        </button>

        <div className="shift-info">
          <Zap size={14} />
          <span>سيتم ربط التأجير بالشيفت الحالي #{currentShift.shift_number || currentShift.id}</span>
        </div>
      </div>
    </div>
  );
});

// ==================== مكون شبكة الألعاب ====================
const GamesGrid = React.memo(({ 
  games, 
  branchId, 
  onAddToCart, 
  loading,
  currentShift
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  // تصفية الألعاب حسب الفرع والحالة
  const branchGames = useMemo(() => {
    if (!games?.length || !branchId) return [];
    return games.filter(game => 
      game && 
      game.branch_id === branchId && 
      game.is_active && 
      game.status === 'available'
    );
  }, [games, branchId]);

  // استخراج الفئات المتاحة
  const categories = useMemo(() => {
    if (!branchGames.length) return [{ value: 'all', label: 'جميع الألعاب', count: 0 }];
    const catMap = new Map();
    branchGames.forEach(game => {
      const cat = game.category || 'أخرى';
      catMap.set(cat, (catMap.get(cat) || 0) + 1);
    });
    return [
      { value: 'all', label: 'جميع الألعاب', count: branchGames.length },
      ...Array.from(catMap.entries()).map(([value, count]) => ({ value, label: value, count }))
    ];
  }, [branchGames]);

  // تصفية الألعاب حسب البحث والفئة
  const filteredGames = useMemo(() => {
    if (!branchGames.length) return [];

    return branchGames.filter(game => {
      // فلترة حسب الفئة
      if (selectedCategory !== 'all' && game.category !== selectedCategory) {
        return false;
      }

      // فلترة حسب البحث
      if (searchTerm) {
        const term = searchTerm.toLowerCase();
        return (
          game.name?.toLowerCase().includes(term) ||
          game.category?.toLowerCase().includes(term) ||
          game.description?.toLowerCase().includes(term)
        );
      }

      return true;
    });
  }, [branchGames, searchTerm, selectedCategory]);

  if (!currentShift) {
    return (
      <div className="games-section disabled">
        <div className="games-header">
          <h3>
            <Gamepad2 size={24} />
            الألعاب المتاحة
          </h3>
        </div>
        <div className="games-disabled-message">
          <Lock size={48} />
          <p>يجب فتح شيفت أولاً لعرض الألعاب</p>
        </div>
      </div>
    );
  }

  if (loading.games) {
    return (
      <div className="games-section">
        <div className="games-header">
          <h3>
            <Gamepad2 size={24} />
            الألعاب المتاحة
          </h3>
        </div>
        <div className="games-loading">
          <Loader2 className="spinner" size={32} />
          <p>جاري تحميل الألعاب...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="games-section">
      <div className="games-header">
        <h3>
          <Gamepad2 size={24} />
          الألعاب المتاحة
          <span className="games-count">({branchGames.length})</span>
        </h3>
        
        <div className="games-filters">
          <div className="search-wrapper">
            <Search size={18} className="search-icon" />
            <input
              type="text"
              placeholder="بحث عن لعبة..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="category-select"
          >
            {categories.map(cat => (
              <option key={cat.value} value={cat.value}>
                {cat.label} ({cat.count})
              </option>
            ))}
          </select>
        </div>
      </div>

      {branchGames.length === 0 ? (
        <div className="games-empty">
          <Gamepad2 size={48} />
          <h4>لا توجد ألعاب متاحة</h4>
          <p>يبدو أن جميع الألعاب مؤجرة أو غير مفعلة</p>
        </div>
      ) : filteredGames.length === 0 ? (
        <div className="games-empty">
          <Search size={48} />
          <p>لا توجد نتائج لـ "{searchTerm}"</p>
        </div>
      ) : (
        <div className="games-grid">
          {filteredGames.map(game => (
            <div
              key={game.id}
              className="game-card"
              onClick={() => onAddToCart(game)}
            >
              <div className="game-card-header">
                <h4>{game.name}</h4>
                {game.category && (
                  <span className="game-category">{game.category}</span>
                )}
              </div>
              
              <div className="game-card-price">
                <DollarSign size={16} />
                <span>{formatCurrency(game.price_per_15min)}</span>
                <span className="price-unit">/ 15 د</span>
              </div>

              <button className="add-to-cart-btn">
                <Plus size={16} />
                إضافة
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
});

// ==================== مكون التأجيرات النشطة ====================
const ActiveRentals = React.memo(({ 
  rentals, 
  items,
  loading, 
  onComplete,
  onCancel,
  onViewDetails,
  currentShift
}) => {
  const [timeNow, setTimeNow] = useState(new Date());
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');

  useEffect(() => {
    const interval = setInterval(() => {
      setTimeNow(new Date());
    }, 1000);
    
    return () => clearInterval(interval);
  }, []);

  // تجميع التأجيرات مع عناصرها
  const rentalsWithItems = useMemo(() => {
    return rentals.map(rental => ({
      ...rental,
      items: items.filter(item => item.rental_id === rental.id)
    }));
  }, [rentals, items]);

  const calculateRemainingTime = useCallback((rental) => {
    if (rental.rental_type === 'open') return 'مفتوح';
    if (!rental.start_time) return '--:--';
    
    const startTime = new Date(rental.start_time);
    const duration = rental.total_duration || 15;
    const endTime = new Date(startTime.getTime() + duration * 60000);
    const remainingMs = endTime - timeNow;
    
    if (remainingMs <= 0) return '00:00';
    
    const remainingMinutes = Math.floor(remainingMs / 60000);
    const remainingSeconds = Math.floor((remainingMs % 60000) / 1000);
    return `${remainingMinutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
  }, [timeNow]);

  const calculateProgress = useCallback((rental) => {
    if (rental.rental_type !== 'fixed' || !rental.start_time) return 100;
    
    const startTime = new Date(rental.start_time);
    const duration = rental.total_duration || 15;
    const endTime = new Date(startTime.getTime() + duration * 60000);
    const totalMs = duration * 60000;
    const remainingMs = endTime - timeNow;
    
    if (remainingMs <= 0) return 0;
    
    const percentage = (remainingMs / totalMs) * 100;
    return Math.max(0, Math.min(100, percentage));
  }, [timeNow]);

  const isExpired = useCallback((rental) => {
    if (rental.rental_type !== 'fixed' || !rental.start_time) return false;
    
    const startTime = new Date(rental.start_time);
    const duration = rental.total_duration || 15;
    const endTime = new Date(startTime.getTime() + duration * 60000);
    
    return timeNow > endTime;
  }, [timeNow]);

  const filteredRentals = useMemo(() => {
    if (!rentalsWithItems.length) return [];
    
    return rentalsWithItems.filter(rental => {
      // فلترة حسب النوع
      if (filterType !== 'all' && rental.rental_type !== filterType) {
        return false;
      }

      // فلترة حسب البحث
      if (searchTerm) {
        const term = searchTerm.toLowerCase();
        return (
          rental.customer_name?.toLowerCase().includes(term) ||
          rental.customer_phone?.includes(term) ||
          rental.rental_number?.toLowerCase().includes(term) ||
          rental.items?.some(item => item.game_name?.toLowerCase().includes(term))
        );
      }

      return true;
    });
  }, [rentalsWithItems, searchTerm, filterType]);

  if (!currentShift) {
    return null;
  }

  if (loading.rentals) {
    return (
      <div className="active-rentals-section">
        <div className="section-header">
          <h3>
            <Activity size={24} />
            التأجيرات النشطة
          </h3>
        </div>
        <div className="loading-state">
          <Loader2 className="spinner" size={32} />
          <p>جاري تحميل التأجيرات...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="active-rentals-section">
      <div className="section-header">
        <h3>
          <Activity size={24} />
          التأجيرات النشطة
          <span className="section-count">({filteredRentals.length})</span>
        </h3>

        <div className="section-filters">
          <div className="search-wrapper small">
            <Search size={16} className="search-icon" />
            <input
              type="text"
              placeholder="بحث..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input small"
            />
          </div>

          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="filter-select small"
          >
            <option value="all">الكل</option>
            <option value="fixed">⏰ ثابت</option>
            <option value="open">🔓 مفتوح</option>
          </select>
        </div>
      </div>

      {filteredRentals.length === 0 ? (
        <div className="empty-state">
          <Activity size={48} />
          <p>لا توجد تأجيرات نشطة</p>
        </div>
      ) : (
        <div className="rentals-grid">
          {filteredRentals.map(rental => (
            <div 
              key={rental.id} 
              className={`rental-card ${isExpired(rental) ? 'expired' : ''} ${rental.rental_type}`}
            >
              <div className="rental-card-header">
                <div className="rental-number">
                  <Hash size={14} />
                  <span>{rental.rental_number}</span>
                </div>
                <span className={`rental-type ${rental.rental_type}`}>
                  {rental.rental_type === 'open' ? '🔓 مفتوح' : '⏰ ثابت'}
                </span>
              </div>

              <div className="rental-customer">
                <User size={16} />
                <span>{rental.customer_name}</span>
                {rental.customer_phone && rental.customer_phone !== '00000000000' && (
                  <span className="customer-phone">({rental.customer_phone})</span>
                )}
              </div>

              <div className="rental-items">
                {rental.items?.map(item => (
                  <div key={item.id} className="rental-item">
                    <Gamepad2 size={14} />
                    <span className="item-name">{item.game_name}</span>
                    {item.child_name && (
                      <span className="child-name">- {item.child_name}</span>
                    )}
                    {item.rental_type === 'fixed' && (
                      <span className="item-duration">{item.duration_minutes} د</span>
                    )}
                    {item.quantity > 1 && (
                      <span className="item-quantity">x{item.quantity}</span>
                    )}
                  </div>
                ))}
              </div>

              <div className="rental-time">
                <Clock size={14} />
                <span>البداية: {formatTime(rental.start_time)}</span>
              </div>

              {rental.rental_type === 'fixed' && (
                <div className="rental-progress">
                  <div className="progress-bar">
                    <div 
                      className={`progress-fill ${isExpired(rental) ? 'expired' : ''}`}
                      style={{ width: `${calculateProgress(rental)}%` }}
                    ></div>
                  </div>
                  <div className="remaining-time">
                    <Hourglass size={14} />
                    <span>المتبقي: {calculateRemainingTime(rental)}</span>
                  </div>
                </div>
              )}

              <div className="rental-actions">
                <button
                  onClick={() => onViewDetails(rental)}
                  className="btn-action info"
                  title="تفاصيل"
                >
                  <Eye size={16} />
                </button>

                {rental.rental_type === 'open' && (
                  <button
                    onClick={() => onComplete(rental)}
                    className="btn-action success"
                    title="إنهاء التأجير"
                  >
                    <Check size={16} />
                  </button>
                )}

                <button
                  onClick={() => onCancel(rental)}
                  className="btn-action danger"
                  title="إلغاء"
                >
                  <X size={16} />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
});

// ==================== مكون إضافة لعبة للسلة ====================
const AddGameModal = React.memo(({
  show,
  onClose,
  games,
  onAddGame
}) => {
  const [searchTerm, setSearchTerm] = useState('');

  const availableGames = useMemo(() => {
    if (!games?.length) return [];
    return games.filter(game => game.is_active && game.status === 'available');
  }, [games]);

  const filteredGames = useMemo(() => {
    if (!availableGames.length) return [];
    
    if (!searchTerm) return availableGames;
    
    const term = searchTerm.toLowerCase();
    return availableGames.filter(game =>
      game.name?.toLowerCase().includes(term) ||
      game.category?.toLowerCase().includes(term)
    );
  }, [availableGames, searchTerm]);

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal modal-large">
        <div className="modal-header">
          <h3>
            <Gamepad2 size={24} />
            إضافة لعبة للسلة
          </h3>
          <button onClick={onClose} className="modal-close">
            <X size={24} />
          </button>
        </div>
        
        <div className="modal-body">
          <div className="search-wrapper large">
            <Search size={20} className="search-icon" />
            <input
              type="text"
              placeholder="ابحث عن لعبة..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input large"
              autoFocus
            />
          </div>

          <div className="games-list">
            {filteredGames.length === 0 ? (
              <div className="empty-state">
                {availableGames.length === 0 ? (
                  <>
                    <Gamepad2 size={48} />
                    <p>لا توجد ألعاب متاحة</p>
                  </>
                ) : (
                  <>
                    <Search size={48} />
                    <p>لا توجد نتائج لـ "{searchTerm}"</p>
                  </>
                )}
              </div>
            ) : (
              filteredGames.map(game => (
                <div
                  key={game.id}
                  className="game-select-item"
                  onClick={() => {
                    onAddGame(game);
                    onClose();
                  }}
                >
                  <div className="game-select-info">
                    <h4>{game.name}</h4>
                    {game.category && (
                      <span className="game-category">{game.category}</span>
                    )}
                    <div className="game-price">
                      {formatCurrency(game.price_per_15min)} / 15 دقيقة
                    </div>
                  </div>
                  <button className="add-btn">
                    <Plus size={20} />
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
});

// ==================== مكون تفاصيل التأجير ====================
const RentalDetailsModal = React.memo(({
  show,
  onClose,
  rental,
  items
}) => {
  if (!show || !rental) return null;

  const rentalItems = items.filter(item => item.rental_id === rental.id);
  const totalDuration = rentalItems.reduce((sum, item) => sum + (item.duration_minutes || 0), 0);

  return (
    <div className="modal-overlay">
      <div className="modal modal-large">
        <div className="modal-header">
          <h3>
            <Eye size={24} />
            تفاصيل التأجير #{rental.rental_number}
          </h3>
          <button onClick={onClose} className="modal-close">
            <X size={24} />
          </button>
        </div>
        
        <div className="modal-body">
          <div className="details-section">
            <h4>
              <User size={18} />
              معلومات العميل
            </h4>
            <div className="details-grid">
              <div className="detail-item">
                <span className="detail-label">الاسم:</span>
                <span className="detail-value">{rental.customer_name}</span>
              </div>
              {rental.customer_phone && rental.customer_phone !== '00000000000' && (
                <div className="detail-item">
                  <span className="detail-label">الهاتف:</span>
                  <span className="detail-value">{rental.customer_phone}</span>
                </div>
              )}
            </div>
          </div>

          <div className="details-section">
            <h4>
              <Clock size={18} />
              معلومات التأجير
            </h4>
            <div className="details-grid">
              <div className="detail-item">
                <span className="detail-label">رقم التأجير:</span>
                <span className="detail-value">{rental.rental_number}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">النوع:</span>
                <span className={`rental-type-badge ${rental.rental_type}`}>
                  {rental.rental_type === 'open' ? '🔓 وقت مفتوح' : '⏰ وقت ثابت'}
                </span>
              </div>
              <div className="detail-item">
                <span className="detail-label">وقت البدء:</span>
                <span className="detail-value">{formatDateTime(rental.start_time)}</span>
              </div>
              {rental.end_time && (
                <div className="detail-item">
                  <span className="detail-label">وقت الانتهاء:</span>
                  <span className="detail-value">{formatDateTime(rental.end_time)}</span>
                </div>
              )}
              <div className="detail-item">
                <span className="detail-label">المدة الإجمالية:</span>
                <span className="detail-value">{totalDuration} دقيقة</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">بواسطة:</span>
                <span className="detail-value">{rental.employee_name || rental.user_name}</span>
              </div>
            </div>
          </div>

          <div className="details-section">
            <h4>
              <Gamepad2 size={18} />
              الألعاب ({rentalItems.length})
            </h4>
            <div className="items-table">
              <table>
                <thead>
                  <tr>
                    <th>اللعبة</th>
                    <th>الطفل</th>
                    <th>المدة</th>
                    <th>الكمية</th>
                    <th>السعر</th>
                  </tr>
                </thead>
                <tbody>
                  {rentalItems.map(item => (
                    <tr key={item.id}>
                      <td>{item.game_name}</td>
                      <td>{item.child_name || '-'}</td>
                      <td>{item.duration_minutes || 0} د</td>
                      <td>{item.quantity || 1}</td>
                      <td>{formatCurrency(item.total_price || 0)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          <div className="details-section">
            <h4>
              <DollarSign size={18} />
              المعلومات المالية
            </h4>
            <div className="details-grid">
              <div className="detail-item">
                <span className="detail-label">المبلغ الإجمالي:</span>
                <span className="detail-value amount">{formatCurrency(rental.total_amount || 0)}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">المبلغ المدفوع:</span>
                <span className="detail-value">{formatCurrency(rental.paid_amount || 0)}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">طريقة الدفع:</span>
                <span className="detail-value">{rental.payment_method || 'كاش'}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">حالة الدفع:</span>
                <span className={`payment-status ${rental.payment_status}`}>
                  {rental.payment_status === 'paid' ? 'مدفوع' : 'معلق'}
                </span>
              </div>
            </div>
          </div>

          {rental.notes && (
            <div className="details-section">
              <h4>
                <Info size={18} />
                ملاحظات
              </h4>
              <p className="notes">{rental.notes}</p>
            </div>
          )}
        </div>

        <div className="modal-footer">
          <button onClick={onClose} className="btn btn-secondary">
            إغلاق
          </button>
          <button className="btn btn-outline">
            <Printer size={16} />
            طباعة
          </button>
        </div>
      </div>
    </div>
  );
});

// ==================== مكون إنهاء تأجير مفتوح ====================
const CompleteOpenRentalModal = React.memo(({
  show,
  onClose,
  rental,
  items,
  onConfirm
}) => {
  const [paymentMethod, setPaymentMethod] = useState('كاش');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const rentalItems = useMemo(() => {
    return items.filter(item => item.rental_id === rental?.id);
  }, [rental, items]);

  const calculateDuration = useCallback(() => {
    if (!rental?.start_time) return 0;
    return Math.max(15, calculateDuration(rental.start_time, new Date()));
  }, [rental]);

  const calculateTotal = useCallback(() => {
    const minutes = calculateDuration();
    return rentalItems.reduce((total, item) => {
      const units = Math.ceil(minutes / 15);
      return total + ((item.price_per_unit || 50) * units * (item.quantity || 1));
    }, 0);
  }, [rentalItems, calculateDuration]);

  const duration = calculateDuration();
  const totalAmount = calculateTotal();

  const handleConfirm = async () => {
    setIsSubmitting(true);
    try {
      await onConfirm(rental, {
        payment_method: paymentMethod,
        final_amount: totalAmount,
        actual_minutes: duration
      });
      onClose();
    } catch (error) {
      console.error('خطأ في إنهاء التأجير:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!show || !rental) return null;

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h3>
            <CheckCircle size={24} />
            إنهاء التأجير المفتوح
          </h3>
          <button onClick={onClose} className="modal-close" disabled={isSubmitting}>
            <X size={24} />
          </button>
        </div>
        
        <div className="modal-body">
          <div className="summary-card">
            <div className="summary-row">
              <span>العميل:</span>
              <span className="summary-value">{rental.customer_name}</span>
            </div>
            <div className="summary-row">
              <span>وقت البدء:</span>
              <span>{formatDateTime(rental.start_time)}</span>
            </div>
            <div className="summary-row highlight">
              <span>المدة المنقضية:</span>
              <span>{duration} دقيقة</span>
            </div>
          </div>

          <div className="items-summary">
            <h4>الألعاب</h4>
            {rentalItems.map(item => (
              <div key={item.id} className="summary-row">
                <span>
                  {item.game_name} 
                  {item.child_name && ` (${item.child_name})`} 
                  {item.quantity > 1 && ` x${item.quantity}`}
                </span>
                <span>{formatCurrency((item.price_per_unit || 50) * Math.ceil(duration / 15) * (item.quantity || 1))}</span>
              </div>
            ))}
          </div>

          <div className="total-section">
            <div className="summary-row total">
              <span>الإجمالي:</span>
              <span className="total-amount">{formatCurrency(totalAmount)}</span>
            </div>
          </div>

          <div className="form-group">
            <label>طريقة الدفع:</label>
            <select
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
              disabled={isSubmitting}
              className="form-control"
            >
              <option value="كاش">💵 نقدي</option>
              <option value="بطاقة">💳 بطاقة</option>
              <option value="محفظة">📱 محفظة إلكترونية</option>
            </select>
          </div>
        </div>

        <div className="modal-footer">
          <button
            onClick={onClose}
            className="btn btn-secondary"
            disabled={isSubmitting}
          >
            إلغاء
          </button>
          <button
            onClick={handleConfirm}
            className="btn btn-success"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <Loader2 className="spinner" size={16} />
                جاري المعالجة...
              </>
            ) : (
              <>
                <Check size={16} />
                تأكيد الإنهاء
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
});

// ==================== المكون الرئيسي ====================
const Rentals = () => {
  const { user } = useAuth();
  
  const [loading, setLoading] = useState({
    games: false,
    rentals: false,
    shift: false,
    processing: false
  });
  
  const [games, setGames] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState(null);
  const [activeRentals, setActiveRentals] = useState([]);
  const [rentalItems, setRentalItems] = useState([]);
  
  // حالة السلة (Cart)
  const [cartItems, setCartItems] = useState([]);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: ''
  });
  
  // حالة النوافذ المنبثقة
  const [showAddGameModal, setShowAddGameModal] = useState(false);
  const [showRentalDetailsModal, setShowRentalDetailsModal] = useState(false);
  const [showCompleteOpenModal, setShowCompleteOpenModal] = useState(false);
  const [selectedRental, setSelectedRental] = useState(null);
  
  const [error, setError] = useState(null);

  // ==================== دوال API ====================

  // تحميل الألعاب
  const loadGames = useCallback(async () => {
    if (!user?.branch_id) return;
    
    setLoading(prev => ({ ...prev, games: true }));
    try {
      const response = await api.get('/games', {
        params: { branch_id: user.branch_id }
      });
      
      if (response.success) {
        setGames(response.data || []);
      }
    } catch (error) {
      console.error('خطأ في تحميل الألعاب:', error);
      setError('فشل تحميل الألعاب');
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  }, [user?.branch_id]);

  // تحميل الشيفت الحالي
  const loadCurrentShift = useCallback(async () => {
    setLoading(prev => ({ ...prev, shift: true }));
    try {
      const response = await api.get('/shifts/current');
      
      if (response.success && response.data) {
        const shift = response.data;
        // التحقق من أن الشيفت يخص المستخدم الحالي
        if (shift.employee_id === user?.id) {
          setCurrentShift(shift);
          await loadShiftStats(shift.id);
        } else {
          setCurrentShift(null);
          setShiftStats(null);
        }
      } else {
        setCurrentShift(null);
        setShiftStats(null);
      }
    } catch (error) {
      console.error('خطأ في تحميل الشيفت:', error);
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  }, [user?.id]);

  // تحميل إحصائيات الشيفت
  const loadShiftStats = useCallback(async (shiftId) => {
    try {
      const response = await api.get(`/shifts/${shiftId}/stats`);
      if (response.success) {
        setShiftStats(response.data);
      }
    } catch (error) {
      console.error('خطأ في تحميل إحصائيات الشيفت:', error);
    }
  }, []);

  // تحميل التأجيرات النشطة
  const loadActiveRentals = useCallback(async () => {
    if (!currentShift?.id) return;
    
    setLoading(prev => ({ ...prev, rentals: true }));
    try {
      const response = await api.get('/rentals/active', {
        params: { shift_id: currentShift.id }
      });
      
      if (response.success) {
        const rentals = response.data || [];
        setActiveRentals(rentals);
        
        // تحميل عناصر التأجيرات
        if (rentals.length > 0) {
          const allItems = [];
          for (const rental of rentals) {
            const itemsResponse = await api.get(`/rental-items`, { 
              params: { rental_id: rental.id } 
            });
            if (itemsResponse.success) {
              allItems.push(...(itemsResponse.data || []));
            }
          }
          setRentalItems(allItems);
        } else {
          setRentalItems([]);
        }
      }
    } catch (error) {
      console.error('خطأ في تحميل التأجيرات:', error);
    } finally {
      setLoading(prev => ({ ...prev, rentals: false }));
    }
  }, [currentShift?.id]);

  // تحديث جميع البيانات
  const refreshAllData = useCallback(async () => {
    await loadCurrentShift();
    if (currentShift?.id) {
      await loadActiveRentals();
      await loadShiftStats(currentShift.id);
    }
    await loadGames();
  }, [loadCurrentShift, loadActiveRentals, loadShiftStats, loadGames, currentShift?.id]);

  // ==================== دوال الشيفت ====================

  const handleStartShift = useCallback(async () => {
    if (!user?.branch_id) {
      alert('لا يوجد فرع محدد للمستخدم');
      return;
    }

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      const response = await api.post('/shifts/start', {
        opening_cash: 0
      });

      if (response.success) {
        setCurrentShift(response.data);
        await refreshAllData();
        alert('✅ تم فتح الشيفت بنجاح');
      } else {
        alert(`❌ فشل فتح الشيفت: ${response.message}`);
      }
    } catch (error) {
      console.error('خطأ في فتح الشيفت:', error);
      alert('حدث خطأ في فتح الشيفت');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [user?.branch_id, refreshAllData]);

  const handleEndShift = useCallback(async (closingCash, notes) => {
    if (!currentShift) return;

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      const response = await api.put(`/shifts/${currentShift.id}/end`, {
        closing_cash: closingCash || shiftStats?.total_revenue || 0,
        notes
      });

      if (response.success) {
        setCurrentShift(null);
        setShiftStats(null);
        setActiveRentals([]);
        setRentalItems([]);
        alert('✅ تم إنهاء الشيفت بنجاح');
      } else {
        alert(`❌ فشل إنهاء الشيفت: ${response.message}`);
      }
    } catch (error) {
      console.error('خطأ في إنهاء الشيفت:', error);
      alert('حدث خطأ في إنهاء الشيفت');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift, shiftStats]);

  // ==================== دوال السلة (Cart) ====================

  const handleAddToCart = useCallback((game) => {
    if (!currentShift) {
      alert('❌ يجب فتح شيفت أولاً');
      return;
    }

    setCartItems(prev => [
      ...prev,
      {
        id: Date.now() + Math.random(),
        game_id: game.id,
        game_name: game.name,
        price_per_15min: game.price_per_15min,
        rental_type: 'fixed',
        duration_minutes: 15,
        quantity: 1,
        child_name: ''
      }
    ]);
  }, [currentShift]);

  const handleUpdateCartItem = useCallback((itemId, updates) => {
    setCartItems(prev =>
      prev.map(item =>
        item.id === itemId ? { ...item, ...updates } : item
      )
    );
  }, []);

  const handleRemoveCartItem = useCallback((itemId) => {
    setCartItems(prev => prev.filter(item => item.id !== itemId));
  }, []);

  const handleClearCart = useCallback(() => {
    setCartItems([]);
    setCustomerInfo({ name: '', phone: '' });
  }, []);

  // ==================== دوال التأجير ====================

  const handleCreateRental = useCallback(async () => {
    if (!currentShift) {
      alert('❌ لا يوجد شيفت نشط');
      return;
    }

    if (cartItems.length === 0) {
      alert('❌ السلة فارغة');
      return;
    }

    if (!customerInfo.name?.trim()) {
      alert('❌ اسم العميل مطلوب');
      return;
    }

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      // تجهيز بيانات التأجير
      const rentalData = {
        customer_name: customerInfo.name.trim(),
        customer_phone: customerInfo.phone || null,
        items: cartItems.map(item => ({
          game_id: item.game_id,
          child_name: item.child_name || null,
          duration_minutes: item.rental_type === 'fixed' ? item.duration_minutes : null,
          quantity: item.quantity,
          rental_type: item.rental_type
        }))
      };

      const response = await api.post('/rentals', rentalData);

      if (response.success) {
        alert(`✅ تم إنشاء التأجير رقم ${response.data.rental_number} بنجاح`);
        handleClearCart();
        await loadActiveRentals();
        await loadShiftStats(currentShift.id);
      } else {
        alert(`❌ فشل إنشاء التأجير: ${response.message}`);
      }
    } catch (error) {
      console.error('خطأ في إنشاء التأجير:', error);
      alert(`❌ فشل إنشاء التأجير: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift, cartItems, customerInfo, handleClearCart, loadActiveRentals, loadShiftStats]);

  const handleCompleteOpenRental = useCallback(async (rental, data) => {
    if (!rental) return;

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      const response = await api.post(`/rentals/${rental.id}/complete-open`, {
        payment_method: data.payment_method,
        actual_minutes: data.actual_minutes,
        final_amount: data.final_amount
      });

      if (response.success) {
        alert(`✅ تم إنهاء التأجير بنجاح\nالمبلغ: ${formatCurrency(data.final_amount)}`);
        await loadActiveRentals();
        await loadShiftStats(currentShift?.id);
      } else {
        alert(`❌ فشل إنهاء التأجير: ${response.message}`);
      }
    } catch (error) {
      console.error('خطأ في إنهاء التأجير:', error);
      alert('حدث خطأ في إنهاء التأجير');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift?.id, loadActiveRentals, loadShiftStats]);

  const handleCancelRental = useCallback(async (rental) => {
    if (!window.confirm('هل أنت متأكد من إلغاء هذا التأجير؟')) return;

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      const response = await api.post(`/rentals/${rental.id}/cancel`);

      if (response.success) {
        alert('✅ تم إلغاء التأجير');
        await loadActiveRentals();
        await loadShiftStats(currentShift?.id);
      } else {
        alert(`❌ فشل إلغاء التأجير: ${response.message}`);
      }
    } catch (error) {
      console.error('خطأ في إلغاء التأجير:', error);
      alert('حدث خطأ في إلغاء التأجير');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift?.id, loadActiveRentals, loadShiftStats]);

  // ==================== Effects ====================

  // تحميل البيانات الأولية
  useEffect(() => {
    refreshAllData();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // تحديث البيانات عند تغيير الشيفت
  useEffect(() => {
    if (currentShift?.id) {
      loadActiveRentals();
      loadShiftStats(currentShift.id);
    }
  }, [currentShift?.id, loadActiveRentals, loadShiftStats]);

  // تحديث دوري للتأجيرات النشطة (كل 30 ثانية)
  useEffect(() => {
    if (!currentShift?.id) return;

    const interval = setInterval(() => {
      loadActiveRentals();
    }, 30000);

    return () => clearInterval(interval);
  }, [currentShift?.id, loadActiveRentals]);

  // ==================== Render ====================

  return (
    <div className="rentals-page">
      <ShiftStatusBar
        currentShift={currentShift}
        shiftStats={shiftStats}
        onStartShift={handleStartShift}
        onEndShift={handleEndShift}
        loading={loading.processing}
      />

      <div className="rentals-content">
        <div className="main-panel">
          <GamesGrid
            games={games}
            branchId={user?.branch_id}
            onAddToCart={handleAddToCart}
            loading={loading}
            currentShift={currentShift}
          />

          <ActiveRentals
            rentals={activeRentals}
            items={rentalItems}
            loading={loading}
            onComplete={(rental) => {
              setSelectedRental(rental);
              setShowCompleteOpenModal(true);
            }}
            onCancel={handleCancelRental}
            onViewDetails={(rental) => {
              setSelectedRental(rental);
              setShowRentalDetailsModal(true);
            }}
            currentShift={currentShift}
          />
        </div>

        <div className="cart-panel">
          <RentalCart
            items={cartItems}
            onUpdateItem={handleUpdateCartItem}
            onRemoveItem={handleRemoveCartItem}
            onClearCart={handleClearCart}
            customerInfo={customerInfo}
            onCustomerInfoChange={(field, value) => 
              setCustomerInfo(prev => ({ ...prev, [field]: value }))
            }
            onSubmit={handleCreateRental}
            isSubmitting={loading.processing}
            games={games}
            onAddGame={() => setShowAddGameModal(true)}
            currentShift={currentShift}
          />
        </div>
      </div>

      <AddGameModal
        show={showAddGameModal}
        onClose={() => setShowAddGameModal(false)}
        games={games}
        onAddGame={handleAddToCart}
      />

      <RentalDetailsModal
        show={showRentalDetailsModal}
        onClose={() => {
          setShowRentalDetailsModal(false);
          setSelectedRental(null);
        }}
        rental={selectedRental}
        items={rentalItems}
      />

      <CompleteOpenRentalModal
        show={showCompleteOpenModal}
        onClose={() => {
          setShowCompleteOpenModal(false);
          setSelectedRental(null);
        }}
        rental={selectedRental}
        items={rentalItems}
        onConfirm={handleCompleteOpenRental}
      />

      {error && (
        <div className="error-toast">
          <AlertCircle size={20} />
          <span>{error}</span>
          <button onClick={() => setError(null)}>
            <X size={16} />
          </button>
        </div>
      )}
    </div>
  );
};

export default Rentals;
