// frontend/src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// ÿ•ŸÜÿ¥ÿßÿ° instance
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// ‚úÖ **ÿ•ÿµŸÑÿßÿ≠ ŸÖŸáŸÖ**: interceptor ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ
api.interceptors.request.use(
  (config) => {
    // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÖŸÜ localStorage
    const token = localStorage.getItem('token');
    
    console.log('üîê Token in interceptor:', token ? 'Exists' : 'Missing');
    
    if (token) {
      // ‚úÖ ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ Ÿäÿ∂ÿßŸÅ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠
      config.headers.Authorization = `Bearer ${token}`;
      console.log('‚úÖ Token added to headers');
    } else {
      console.warn('‚ö†Ô∏è No token found in localStorage');
    }
    
    return config;
  },
  (error) => {
    console.error('‚ùå Request interceptor error:', error);
    return Promise.reject(error);
  }
);

// interceptor ŸÑŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ ${response.config.method?.toUpperCase()} ${response.config.url}:`, response.status);
    return response;
  },
  (error) => {
    console.error('‚ùå Response error:', {
      message: error.message,
      status: error.response?.status,
      url: error.config?.url,
      method: error.config?.method
    });
    
    if (error.response?.status === 401) {
      console.log('üîí Unauthorized - clearing storage');
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    
    if (error.response?.status === 403) {
      console.log('üö´ Forbidden -‰Ω†ÂèØËÉΩ ŸÑÿß ÿ™ŸÖŸÑŸÉ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™');
      alert('ÿ£ŸÜÿ™ ŸÑÿß ÿ™ŸÖŸÑŸÉ ÿµŸÑÿßÿ≠Ÿäÿ© ÿßŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ Ÿáÿ∞Ÿá ÿßŸÑÿµŸÅÿ≠ÿ©');
    }
    
    return Promise.reject(error);
  }
);

export default api;
