import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 ÿ¨.ŸÖ';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone) return false;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

 const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  const [showFixModal, setShowFixModal] = useState(false);

  
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

const loadGames = async () => {
  try {
    console.log('üéÆ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÑŸÅÿ±ÿπ', user?.branch_id);
    
    if (!user?.branch_id) {
      console.log('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ŸÅÿ±ÿπ ŸÖÿ≠ÿØÿØ');
      setGames([]);
      return;
    }
    
    // ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿØÿßŸÑÿ© ÿßŸÑŸÖÿ≠ÿ≥ŸÜÿ©
    const response = await api.loadGamesSmart(user.branch_id);
      
    if (response.success) {
      console.log(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${response.data.length} ŸÑÿπÿ®ÿ©`);
      setGames(response.data);
      setAllGames(response.data);
      setStats(prev => ({
        ...prev,
        availableGames: response.data.length,
        totalGames: response.data.length
      }));
    } else {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', response.message);
      setGames([]);
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
    setGames([]);
  } finally {
    setLoading(prev => ({ ...prev, games: false }));
  }
};

const showSuccess = (message) => {
  alert(`‚úÖ ${message}`);
};

const showError = (message) => {
  alert(`‚ùå ${message}`);
};

const showInfo = (message) => {
  alert(`‚ÑπÔ∏è ${message}`);
};

const loadCurrentShift = async () => {
  try {
    const response = await api.get('/shifts/simple');
    
    if (response.success && response.data) {
      if (response.data.employee_id === user?.id || 
          response.data.employee_name === user?.name) {
        setCurrentShift(response.data);
      } else {
        setCurrentShift(null);
      }
    } else {
      setCurrentShift(null);
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    setCurrentShift(null);
  } finally {
    setLoading(prev => ({ ...prev, shift: false }));
  }
};

const loadCompletedRentals = async () => {
  if (!isManager) return;
  
  setLoading(prev => ({ ...prev, stats: true }));
  
  try {
    let response;
    
    if (currentShift && currentShift.id) {
      response = await api.get('/rentals/completed-only', {
        params: {
          shift_id: currentShift.id,
          branch_id: user?.branch_id
        }
      });
    } else {
      setCompletedRentals([]);
      setStats(prev => ({
        ...prev,
        todayRevenue: 0,
        todayRentals: 0
      }));
      return;
    }
    
    if (response.success && response.data) {
      const rentals = Array.isArray(response.data) ? response.data : [];
      
      setCompletedRentals(rentals);
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© (ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠ ŸÅŸÇÿ∑)
      let completedRevenue = 0;
      rentals.forEach(rental => {
        if (rental.rental_type === 'open' || rental.is_open_time === 1) {
          completedRevenue += parseFloat(rental.final_amount || 0);
        }
      });
      
      setStats(prev => ({
        ...prev,
        todayRevenue: completedRevenue,
        todayRentals: rentals.length
      }));
    } else {
      setCompletedRentals([]);
      setStats(prev => ({
        ...prev,
        todayRevenue: 0,
        todayRentals: 0
      }));
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
    setCompletedRentals([]);
    setStats(prev => ({
      ...prev,
      todayRevenue: 0,
      todayRentals: 0
    }));
  } finally {
    setLoading(prev => ({ ...prev, stats: false }));
  }
};

const refreshShiftRevenue = async () => {
  if (!currentShift || !currentShift.id) {
    alert('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑');
    return;
  }
  
  try {
    const response = await api.get(`/shifts/${currentShift.id}/revenue`);
    
    if (response.success && response.data) {
      const revenue = response.data.total_revenue || 0;
      setStats(prev => ({
        ...prev,
        todayRevenue: revenue
      }));
      
      alert(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ¥ŸäŸÅÿ™\nÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ≠ÿßŸÑŸä: ${formatCurrency(revenue)}`);
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ:', error);
    alert('‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ');
  }
};

// ÿØÿßŸÑÿ© ŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ≠ŸÇŸäŸÇŸä ŸÑŸÑÿ¥ŸäŸÅÿ™
const calculateRealRevenue = async () => {
  if (!currentShift || !currentShift.id) return 0;
  
  try {
    // ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id
      }
    });
    
    if (response.success && response.data) {
      const rentals = Array.isArray(response.data) ? response.data : [];
      
      let totalRevenue = 0;
      
      rentals.forEach(rental => {
        if (rental.status === 'ŸÜÿ¥ÿ∑' || rental.status === 'ŸÖŸÉÿ™ŸÖŸÑ') {
          if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
            // ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™: ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã
            totalRevenue += parseFloat(rental.paid_amount || rental.total_amount || 0);
          } else if (rental.status === 'ŸÖŸÉÿ™ŸÖŸÑ' && (rental.rental_type === 'open' || rental.is_open_time === 1)) {
            // ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑ: ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÜŸáÿßÿ¶Ÿä
            totalRevenue += parseFloat(rental.final_amount || rental.total_amount || 0);
          }
        }
      });
      
      return totalRevenue;
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ≠ŸÇŸäŸÇŸä:', error);
  }
  
  return 0;
};

// ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä ŸÑŸÑÿ•Ÿäÿ±ÿßÿØ
const updateRevenueAutomatically = async () => {
  if (currentShift && currentShift.id) {
    const realRevenue = await calculateRealRevenue();
    setStats(prev => ({
      ...prev,
      todayRevenue: realRevenue
    }));
  }
};

useEffect(() => {
  if (currentShift && currentShift.id && !loading.shift) {
    if (loading.rentals) {
      loadActiveRentals();
    }
    
    if (isManager && loading.stats) {
      loadCompletedRentals();
    }
  }
}, [currentShift?.id, loading.shift, isManager]);

// ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
useEffect(() => {
  const updateRevenue = async () => {
    if (currentShift && currentShift.id) {
      const realRevenue = await calculateRealRevenue();
      setStats(prev => ({
        ...prev,
        todayRevenue: realRevenue
      }));
    }
  };
  
  updateRevenue();
}, [currentShift?.id, activeRentals, completedRentals]);

const calculateCurrentShiftRevenue = (rentals) => {
  let shiftRevenue = 0;
  let shiftRentals = rentals?.length || 0;
  
  if (rentals && rentals.length > 0) {
    shiftRevenue = rentals.reduce((sum, rental) => {
      const amount = rental.final_amount || rental.total_amount || 0;
      return sum + (parseFloat(amount) || 0);
    }, 0);
  }
  
  setStats(prev => ({
    ...prev,
    todayRevenue: shiftRevenue,
    todayRentals: shiftRentals
  }));
};

const calculateRemainingTimeValue = (rental) => { 
  if (!rental) return 0;
  
  const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
  const now = new Date();
  const startTime = new Date(rental.start_time);
  
  if (isOpenTime) {
    const elapsedMinutes = Math.floor((now - startTime) / 60000);
    const units = Math.ceil(elapsedMinutes / 15);
    const pricePer15Min = rental.price_per_15min || 100;
    return units * pricePer15Min;
  } else {
    return rental.total_amount || rental.final_amount || 0;
  }
};

const loadAllData = useCallback(async () => {
  setLoading({ games: true, rentals: true, shift: true, stats: true });
  
  try {
    await loadGames();
    await loadCurrentShift();
    
    if (currentShift && currentShift.id) {
      try {
        const response = await api.get('/rentals/active');
        if (response.success) {
          setActiveRentals(response.data || []);
        }
      } catch (activeError) {
        console.warn('‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', activeError.message);
      }
      
      if (isManager) {
        try {
          const response = await api.get('/rentals', {
            params: {
              status: 'ŸÖŸÉÿ™ŸÖŸÑ',
              limit: 50,
              branch_id: user?.branch_id
            }
          });
          
          if (response.success) {
            const shiftRentals = response.data?.filter(rental => 
              rental.shift_id === currentShift.id
            ) || [];
            
            setCompletedRentals(shiftRentals);
            
            let shiftRevenue = 0;
            if (shiftRentals.length > 0) {
              shiftRevenue = shiftRentals.reduce((sum, rental) => {
                return sum + (parseFloat(rental.final_amount || rental.total_amount || 0));
              }, 0);
            }
            
            setStats(prev => ({
              ...prev,
              todayRevenue: shiftRevenue,
              todayRentals: shiftRentals.length,
              activeRentals: activeRentals.length
            }));
          }
        } catch (completedError) {
          console.warn('‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', completedError.message);
        }
      }
    }
  } catch (error) {
