const calculateItemPrice = useCallback((item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return (item.pricePer15min * units * item.quantity) || 0;
}, []);

const handleEndRental = async (rental) => {
  try {
    if (!rental || !rental.id) {
      alert('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©');
      return;
    }
    
    const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
    
    if (isOpenTime) {
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const minutes = Math.floor((now - startTime) / 60000);
      const units = Math.ceil(Math.max(minutes, 15) / 15); // ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ÿØŸÜŸâ 15 ÿØŸÇŸäŸÇÿ©
      const pricePer15Min = rental.price_per_15min || 100;
      const estimatedAmount = units * pricePer15Min;
      
      if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠ÿü\n\nÿßŸÑŸÖÿØÿ©: ${minutes} ÿØŸÇŸäŸÇÿ©\nÿßŸÑÿ≥ÿπÿ± ÿßŸÑÿ™ŸÇÿØŸäÿ±Ÿä: ${formatCurrency(estimatedAmount)}`)) {
        return;
      }
      
      const response = await api.completeRental(rental.id, {
        payment_method: 'ŸÉÿßÿ¥',
        final_amount: estimatedAmount
      });
      
      if (response.success) {
        // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿπŸÜÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠
        try {
          await api.post('/revenue/add-rental', {
            shift_id: currentShift?.id,
            rental_id: rental.id,
            amount: estimatedAmount,
            payment_method: 'ŸÉÿßÿ¥',
            notes: `ÿ•Ÿäÿ±ÿßÿØ ÿ™ÿ£ÿ¨Ÿäÿ± ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠ - ${rental.game_name}`
          });
        } catch (revError) {
          console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ∂ÿßŸÅÿ© ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠:', revError);
        }
        
        alert('‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
        loadActiveRentals();
        loadCompletedRentals();
        
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ŸÖÿ®ÿßÿ¥ÿ±ÿ©
        setStats(prev => ({
          ...prev,
          todayRevenue: (prev.todayRevenue || 0) + estimatedAmount
        }));
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
      }
    } else {
      alert('ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ ŸäÿØŸàŸäÿßŸã');
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
    alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
  }
};

const validateShiftBeforeRental = async () => {
  try {
    if (!currentShift) {
      const shiftResponse = await api.get('/shifts/simple');
      
      if (shiftResponse.success && shiftResponse.data) {
        if (shiftResponse.data.employee_id === user?.id || 
            shiftResponse.data.employee_name === user?.name) {
          setCurrentShift(shiftResponse.data);
          return true;
        }
      }
    }
    
    if (currentShift) {
      if (currentShift.employee_id === user?.id || 
          currentShift.employee_name === user?.name) {
        return true;
      } else {
        console.warn('‚ö†Ô∏è ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÑÿß ŸäŸÜÿ™ŸÖŸä ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', {
          shiftEmployee: currentShift.employee_name,
          currentUser: user?.name
        });
      }
    }
    
    const confirmed = window.confirm(
      'ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ ŸÑŸÉ. ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØÿü'
    );
    
    if (confirmed) {
      await startShift();
      setTimeout(() => {}, 500);
      return true;
    }
    
    return false;
    
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    
    const confirmed = window.confirm(
      'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™. ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØÿü'
    );
    
    if (confirmed) {
      await startShift();
      return true;
    }
    
    return false;
  }
};

