const handleEarlyEndRental = async (rental) => {
  try {
    if (!rental || !rental.id) {
      alert('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©');
      return;
    }
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ŸÜŸàÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
    const isFixedTime = rental.rental_type === 'fixed';
    
    if (!isFixedTime) {
      alert('ÿßŸÑÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ± ÿÆÿßÿµ ÿ®ÿßŸÑÿ£ŸàŸÇÿßÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ÿ© ŸÅŸÇÿ∑');
      return;
    }
    
    const startTime = new Date(rental.start_time);
    const now = new Date();
    const elapsedMinutes = Math.floor((now - startTime) / 60000);
    
    if (elapsedMinutes > 3) {
      alert(`‚ùå ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ÿπÿØ ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ\nÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä: ${elapsedMinutes} ÿØŸÇŸäŸÇÿ©`);
      return;
    }
    
    const paidAmount = rental.paid_amount || rental.total_amount || 0;
    
    const confirmMessage = `
    ÿ™ÿ£ŸÉŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ®ŸÉÿ± (ŸÅŸä ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ):
    
    ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:
    - ÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}
    - ÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}
    - ÿßŸÑŸÖÿØÿ© ÿßŸÑÿ£ÿµŸÑŸäÿ©: ${rental.duration_minutes || 15} ÿØŸÇŸäŸÇÿ©
    - ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ: ${elapsedMinutes} ÿØŸÇŸäŸÇÿ©
    - ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿØŸÅŸàÿπ: ${formatCurrency(paidAmount)}
    
    ‚ö†Ô∏è ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™ ŸÖŸáŸÖÿ©:
    1. ÿ≥Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿ±ÿØÿßÿØ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿ®ÿßŸÑŸÉÿßŸÖŸÑ ŸÑŸÑÿπŸÖŸäŸÑ
    2. ŸÑŸÜ Ÿäÿ∂ÿßŸÅ ÿ£Ÿä ÿ•Ÿäÿ±ÿßÿØ ŸÑŸÑÿ¥ŸäŸÅÿ™
    3. ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿ≥Ÿäÿ™ŸÖ ÿÆÿµŸÖŸá ŸÖŸÜ ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
    `;
    
    if (!window.confirm(confirmMessage)) {
      return;
    }
    
    const response = await api.post(`/rentals/${rental.id}/cancel-early`, {
      reason: 'ÿ•ŸÑÿ∫ÿßÿ° ŸÖÿ®ŸÉÿ± ÿ®Ÿàÿßÿ≥ÿ∑ÿ© ÿßŸÑŸÖŸàÿ∏ŸÅ'
    });
    
    if (response.success) {
      alert(`‚úÖ ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠\nÿ™ŸÖ ÿßÿ≥ÿ™ÿ±ÿØÿßÿØ ${formatCurrency(response.data.refund_amount)} ŸÑŸÑÿπŸÖŸäŸÑ`);
      
      // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      loadActiveRentals();
      loadCompletedRentals();
      loadGames();
    } else {
      alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ±:', error);
