        loadAllData();
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
