    if (!customerInfo.name || !customerInfo.phone) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ Ÿàÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ');
