import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Search, RefreshCw, Eye, XCircle, User, Calendar,
  DollarSign, ChevronLeft, ChevronRight, X,
  Clock, Smartphone, Download, BarChart3, History,
  Activity, FilterX, AlertCircle, CheckCircle,
  Loader2, AlertTriangle
} from 'lucide-react';
import api from '../../services/api';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== Refs ====================
  const isMounted = useRef(true);
  const loadingRef = useRef(false);
  const initialLoadDone = useRef(false);
  
  // ==================== Ø§Ù„Ø­Ø§Ù„Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ© ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [apiError, setApiError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('checking');
  const [hasMore, setHasMore] = useState(false);
  const [nextPage, setNextPage] = useState(1);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…
  const [currentUser, setCurrentUser] = useState(null);
  const [branches, setBranches] = useState([]);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØªØ§Øª
  const [shifts, setShifts] = useState([]);
  const [selectedShift, setSelectedShift] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  
  // Ø­Ø§Ù„Ø© Ø§Ù„Ù†ÙˆØ§ÙØ°
  const [showRentalDetailsModal, setShowRentalDetailsModal] = useState(false);
  const [selectedRental, setSelectedRental] = useState(null);
  const [showShiftStatsModal, setShowShiftStatsModal] = useState(false);
  
  // Ø§Ù„ÙÙ„Ø§ØªØ±
  const [filters, setFilters] = useState({
    status: 'all',
    payment_status: 'all',
    dateFrom: '',
    dateTo: '',
    branch_id: 'all'
  });
  
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [stats, setStats] = useState({
    totalRentals: 0,
    activeRentals: 0,
    completedRentals: 0,
    cancelledRentals: 0,
    totalRevenue: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ==================== Ø¯ÙˆØ§Ù„ Ù…Ø³Ø§Ø¹Ø¯Ø© ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num).replace('EGP', 'Ø¬.Ù…');
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '-';
      return date.toLocaleString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '-';
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '-';
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const getStatusBadge = (status) => {
    switch(status?.toLowerCase?.()) {
      case 'Ù†Ø´Ø·':
      case 'active':
        return <span className="status-badge active"><span className="status-dot active-dot"></span> Ù†Ø´Ø·</span>;
      case 'Ù…ÙƒØªÙ…Ù„':
      case 'completed':
        return <span className="status-badge completed"><span className="status-dot completed-dot"></span> Ù…ÙƒØªÙ…Ù„</span>;
      case 'Ù…Ù„ØºÙŠ':
      case 'cancelled':
        return <span className="status-badge cancelled"><span className="status-dot cancelled-dot"></span> Ù…Ù„ØºÙŠ</span>;
      default:
        return <span className="status-badge">{status || 'ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ'}</span>;
    }
  };

  const getPaymentStatusBadge = (status) => {
    switch(status?.toLowerCase?.()) {
      case 'Ù…Ø¯ÙÙˆØ¹':
      case 'paid':
        return <span className="payment-badge paid"><span className="payment-dot paid-dot"></span> Ù…Ø¯ÙÙˆØ¹</span>;
      case 'ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹':
      case 'unpaid':
        return <span className="payment-badge unpaid"><span className="payment-dot unpaid-dot"></span> ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹</span>;
      case 'Ø¬Ø²Ø¦ÙŠ':
      case 'partial':
        return <span className="payment-badge partial"><span className="payment-dot partial-dot"></span> Ø¬Ø²Ø¦ÙŠ</span>;
      case 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡':
      case 'pending':
        return <span className="payment-badge pending"><span className="payment-dot pending-dot"></span> Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡</span>;
      default:
        return <span className="payment-badge">{status || 'ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ'}</span>;
    }
  };

  const getRentalType = (rental) => {
    if (!rental) return 'fixed';
    
    if (rental.rental_type === 'open' || rental.is_open_time === 1) {
      return 'open';
    }
    
    if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
      return 'fixed';
    }
    
    if (rental.payment_status === 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡' || rental.payment_status === 'pending') {
      return 'open';
    }
    
    return 'fixed';
  };

  const getRentalTypeBadge = (rental) => {
    const type = getRentalType(rental);
    return type === 'open' ? 
      <span className="type-badge open">â±ï¸ ÙˆÙ‚Øª Ù…ÙØªÙˆØ­</span> : 
      <span className="type-badge fixed">â° ÙˆÙ‚Øª Ø«Ø§Ø¨Øª</span>;
  };

  // ==================== Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª ====================
  const calculateStats = useCallback((rentalsData) => {
    const active = rentalsData.filter(r => 
      r.status === 'Ù†Ø´Ø·' || r.status?.toLowerCase() === 'active'
    ).length;
    
    const completed = rentalsData.filter(r => 
      r.status === 'Ù…ÙƒØªÙ…Ù„' || r.status?.toLowerCase() === 'completed'
    ).length;
    
    const cancelled = rentalsData.filter(r => 
      r.status === 'Ù…Ù„ØºÙŠ' || r.status?.toLowerCase() === 'cancelled'
    ).length;
    
    const totalRevenue = rentalsData
      .filter(r => r.payment_status === 'Ù…Ø¯ÙÙˆØ¹' || r.payment_status === 'paid')
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    const today = new Date().toISOString().split('T')[0];
    const todayRentals = rentalsData.filter(r => 
      r.start_time?.split('T')[0] === today
    ).length;
    
    const todayRevenue = rentalsData
      .filter(r => r.start_time?.split('T')[0] === today && 
                   (r.payment_status === 'Ù…Ø¯ÙÙˆØ¹' || r.payment_status === 'paid'))
      .reduce((sum, r) => sum + (parseFloat(r.final_amount) || parseFloat(r.total_amount) || 0), 0);
    
    setStats({
      totalRentals: rentalsData.length,
      activeRentals: active,
      completedRentals: completed,
      cancelledRentals: cancelled,
      totalRevenue,
      todayRevenue,
      todayRentals
    });
  }, []);

  // ==================== ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ù† API ====================
  const loadRentals = useCallback(async (page = 1, append = false) => {
    // Ù…Ù†Ø¹ Ø§Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ù…ØªÙƒØ±Ø±
    if (loadingRef.current) {
      console.log('â³ ØªØ­Ù…ÙŠÙ„ Ø¬Ø§Ø±ÙŠ Ø¨Ø§Ù„ÙØ¹Ù„...');
      return;
    }
    
    loadingRef.current = true;
    
    if (append) {
      setLoadingMore(true);
    } else {
      setLoading(true);
    }
    
    setApiError(null);
    
    try {
      console.log(`ðŸ“‹ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª - Ø§Ù„ØµÙØ­Ø© ${page}`);
      
      // Ø§Ù„Ø­ØµÙˆÙ„ Ø¹Ù„Ù‰ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø­Ø§Ù„ÙŠ
      const user = api.getCurrentUser();
      setCurrentUser(user);
      
      // Ø¥Ø¹Ø¯Ø§Ø¯ Ø§Ù„Ø¨Ø§Ø±Ø§Ù…ÙŠØªØ±Ø§Øª
      const params = {
        page: page,
        limit: 50,
        order_by: 'start_time',
        order_direction: 'DESC'
      };
      
      // Ø¥Ø¶Ø§ÙØ© ÙÙ„ØªØ± Ø§Ù„ÙØ±Ø¹ Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø¹Ø§Ø¯ÙŠ
      if (user?.branch_id && user?.role !== 'admin') {
        params.branch_id = user.branch_id;
      }
      
      // Ø¥Ø¶Ø§ÙØ© Ø§Ù„ÙÙ„Ø§ØªØ±
      if (filters.status && filters.status !== 'all') {
        params.status = filters.status;
      }
      
      if (filters.payment_status && filters.payment_status !== 'all') {
        params.payment_status = filters.payment_status;
      }
      
      if (filters.dateFrom) {
        params.start_date = filters.dateFrom;
      }
      
      if (filters.dateTo) {
        params.end_date = filters.dateTo;
      }
      
      if (filters.branch_id && filters.branch_id !== 'all' && user?.role === 'admin') {
        params.branch_id = filters.branch_id;
      }
      
      // Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      const response = await api.getRentals(params);
      
      if (!isMounted.current) return;
      
      if (response.success && response.data) {
        console.log(`âœ… ØªÙ… Ø¬Ù„Ø¨ ${response.data.length} ØªØ£Ø¬ÙŠØ±`);
        
        // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
        const processedRentals = response.data.map(rental => ({
          id: rental.id,
          rental_number: rental.rental_number || `#${rental.id}`,
          game_id: rental.game_id,
          game_name: rental.game_name || rental.game?.name || 'Ù„Ø¹Ø¨Ø© ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙØ©',
          customer_name: rental.customer_name || 'Ø¹Ù…ÙŠÙ„',
          customer_phone: rental.customer_phone || 'ØºÙŠØ± Ù…ØªÙˆÙØ±',
          employee_id: rental.user_id || rental.employee_id,
          employee_name: rental.employee_name || rental.employee?.name || 'Ù…ÙˆØ¸Ù',
          branch_id: rental.branch_id || user?.branch_id || 1,
          branch_name: rental.branch_name || rental.branch?.name || `ÙØ±Ø¹ ${rental.branch_id || user?.branch_id || 1}`,
          shift_id: rental.shift_id,
          shift_number: rental.shift_number,
          start_time: rental.start_time,
          end_time: rental.end_time,
          actual_end_time: rental.actual_end_time,
          rental_type: rental.rental_type || (rental.is_open_time === 1 ? 'open' : 'fixed'),
          is_open_time: rental.is_open_time || 0,
          duration_minutes: rental.duration_minutes || 15,
          actual_duration_minutes: rental.actual_duration_minutes,
          total_amount: parseFloat(rental.total_amount) || 0,
          final_amount: parseFloat(rental.final_amount) || parseFloat(rental.total_amount) || 0,
          paid_amount: parseFloat(rental.paid_amount) || 0,
          payment_status: rental.payment_status || 'ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹',
          payment_method: rental.payment_method || 'ÙƒØ§Ø´',
          status: rental.status || 'Ù†Ø´Ø·',
          notes: rental.notes,
          created_at: rental.created_at,
          updated_at: rental.updated_at
        }));
        
        if (append) {
          setRentals(prev => [...prev, ...processedRentals]);
          setFilteredRentals(prev => [...prev, ...processedRentals]);
        } else {
          setRentals(processedRentals);
          setFilteredRentals(processedRentals);
        }
        
        // ØªØ­Ø¯ÙŠØ« Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ØµÙØ­Ø§Øª
        setHasMore(response.has_more || false);
        setNextPage(response.next_page || page + 1);
        
        // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
        const allRentals = append ? [...rentals, ...processedRentals] : processedRentals;
        calculateStats(allRentals);
        
        // Ø¬Ù„Ø¨ Ø§Ù„Ø´ÙŠÙØªØ§Øª Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
        extractShiftsFromRentals(allRentals);
        
        setConnectionStatus('connected');
      } else {
        console.warn('âš ï¸ API Ø§Ø³ØªØ¬Ø§Ø¨ Ø¨Ø¯ÙˆÙ† Ø¨ÙŠØ§Ù†Ø§Øª');
        if (!append) {
          setRentals([]);
          setFilteredRentals([]);
          calculateStats([]);
        }
        setConnectionStatus('error');
        setApiError(response.message || 'Ù„Ø§ ØªÙˆØ¬Ø¯ Ø¨ÙŠØ§Ù†Ø§Øª');
      }
      
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', error);
      
      if (isMounted.current) {
        setApiError('ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª: ' + (error.message || 'Ø®Ø·Ø£ ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ'));
        setConnectionStatus('error');
        
        if (!append) {
          setRentals([]);
          setFilteredRentals([]);
          calculateStats([]);
        }
      }
      
    } finally {
      if (isMounted.current) {
        setLoading(false);
        setLoadingMore(false);
        loadingRef.current = false;
        initialLoadDone.current = true;
      }
    }
  }, [filters, calculateStats]);

  // Ø§Ø³ØªØ®Ø±Ø§Ø¬ Ø§Ù„Ø´ÙŠÙØªØ§Øª Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const extractShiftsFromRentals = (rentalsData) => {
    const shiftMap = new Map();
    
    rentalsData.forEach(rental => {
      if (rental.shift_id) {
        const key = rental.shift_id;
        
        if (!shiftMap.has(key)) {
          shiftMap.set(key, {
            id: rental.shift_id,
            shift_number: rental.shift_number,
            employee_id: rental.employee_id,
            employee_name: rental.employee_name,
            branch_id: rental.branch_id,
            branch_name: rental.branch_name,
            start_time: rental.start_time,
            total_rentals: 0,
            total_revenue: 0,
            active_rentals: 0
          });
        }
        
        const shift = shiftMap.get(key);
        shift.total_rentals++;
        
        if (rental.status === 'Ù…ÙƒØªÙ…Ù„' || rental.status === 'completed') {
          shift.total_revenue += parseFloat(rental.final_amount) || parseFloat(rental.total_amount) || 0;
        }
        
        if (rental.status === 'Ù†Ø´Ø·' || rental.status === 'active') {
          shift.active_rentals++;
        }
      }
    });
    
    const shiftsArray = Array.from(shiftMap.values())
      .sort((a, b) => new Date(b.start_time) - new Date(a.start_time))
      .slice(0, 10);
    
    setShifts(shiftsArray);
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¥Ø¶Ø§ÙÙŠØ©
  const loadAdditionalData = useCallback(async () => {
    try {
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹ Ø¥Ø°Ø§ ÙƒØ§Ù† Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ù…Ø¯ÙŠØ±Ø§Ù‹
      const user = api.getCurrentUser();
      
      if (user?.role === 'admin') {
        const branchesRes = await api.getBranches();
        if (branchesRes.success && branchesRes.data) {
          setBranches(branchesRes.data);
        }
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ
      const shiftRes = await api.getCurrentShift();
      if (shiftRes.success && shiftRes.data) {
        setCurrentShift(shiftRes.data);
      }
      
    } catch (error) {
      console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¥Ø¶Ø§ÙÙŠØ©:', error.message);
    }
  }, []);

  // ==================== Ø§Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£ÙˆÙ„ÙŠ ====================
  useEffect(() => {
    isMounted.current = true;
    
    const initialize = async () => {
      await loadRentals(1, false);
      await loadAdditionalData();
    };
    
    initialize();
    
    return () => {
      isMounted.current = false;
    };
  }, [loadRentals, loadAdditionalData]);

  // ==================== Ø§Ù„ÙÙ„ØªØ±Ø© ÙˆØ§Ù„Ø¨Ø­Ø« ====================
  useEffect(() => {
    if (rentals.length > 0) {
      applyFilters();
    }
  }, [rentals, filters, searchTerm, selectedShift]);

  const applyFilters = () => {
    let result = [...rentals];
    
    // ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø§Ù„Ø´ÙŠÙØª
    if (selectedShift) {
      result = result.filter(r => r.shift_id == selectedShift.id);
    }
    
    // ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø§Ù„Ø¨Ø­Ø«
    if (searchTerm.trim()) {
      const term = searchTerm.toLowerCase().trim();
      result = result.filter(rental => 
        (rental.customer_name?.toLowerCase().includes(term) || '') ||
        (rental.customer_phone?.includes(term) || '') ||
        (rental.game_name?.toLowerCase().includes(term) || '') ||
        (rental.employee_name?.toLowerCase().includes(term) || '') ||
        (rental.rental_number?.toLowerCase().includes(term) || '')
      );
    }
    
    // ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø§Ù„Ø­Ø§Ù„Ø©
    if (filters.status && filters.status !== 'all') {
      result = result.filter(r => {
        const status = r.status?.toLowerCase() || '';
        const filterStatus = filters.status.toLowerCase();
        return status === filterStatus || 
               (filterStatus === 'Ù†Ø´Ø·' && status === 'active') ||
               (filterStatus === 'Ù…ÙƒØªÙ…Ù„' && status === 'completed') ||
               (filterStatus === 'Ù…Ù„ØºÙŠ' && status === 'cancelled');
      });
    }
    
    // ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø­Ø§Ù„Ø© Ø§Ù„Ø¯ÙØ¹
    if (filters.payment_status && filters.payment_status !== 'all') {
      result = result.filter(r => {
        const payment = r.payment_status?.toLowerCase() || '';
        const filterPayment = filters.payment_status.toLowerCase();
        return payment === filterPayment ||
               (filterPayment === 'Ù…Ø¯ÙÙˆØ¹' && payment === 'paid') ||
               (filterPayment === 'ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹' && payment === 'unpaid') ||
               (filterPayment === 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡' && payment === 'pending');
      });
    }
    
    // ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø§Ù„ØªØ§Ø±ÙŠØ®
    if (filters.dateFrom) {
      result = result.filter(r => r.start_time?.split('T')[0] >= filters.dateFrom);
    }
    if (filters.dateTo) {
      result = result.filter(r => r.start_time?.split('T')[0] <= filters.dateTo);
    }
    
    // ÙÙ„ØªØ±Ø© Ø­Ø³Ø¨ Ø§Ù„ÙØ±Ø¹ (Ù„Ù„Ù…Ø¯ÙŠØ±)
    if (filters.branch_id && filters.branch_id !== 'all') {
      result = result.filter(r => r.branch_id == filters.branch_id);
    }
    
    setFilteredRentals(result);
    setCurrentPage(1);
  };

  const resetFilters = () => {
    setFilters({
      status: 'all',
      payment_status: 'all',
      dateFrom: '',
      dateTo: '',
      branch_id: currentUser?.role === 'admin' ? 'all' : currentUser?.branch_id || 'all'
    });
    setSearchTerm('');
    setSelectedShift(null);
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ù…Ø²ÙŠØ¯ Ù…Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  const loadMore = () => {
    if (hasMore && !loadingMore) {
      loadRentals(nextPage, true);
    }
  };

