import React, { useState, useEffect, useCallback, useRef } from 'react';
import { 
  Search, RefreshCw, Eye, XCircle, User, Calendar,
  DollarSign, ChevronLeft, ChevronRight, X,
  Clock, Smartphone, Download, BarChart3, History,
  Activity, FilterX, AlertCircle
} from 'lucide-react';
import api from '../../services/api';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== Refs Ù„Ù…Ù†Ø¹ Ø§Ù„ØªÙƒØ±Ø§Ø± ====================
  const isMounted = useRef(true);
  const loadingRef = useRef(false);
  const loadCount = useRef(0);
  const initialLoadDone = useRef(false);
  
  // ==================== Ø§Ù„Ø­Ø§Ù„Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ© ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [apiError, setApiError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('checking');
  const [usingMockData, setUsingMockData] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…
  const [currentUser, setCurrentUser] = useState(null);
  const [branches, setBranches] = useState([]);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØªØ§Øª
  const [shifts, setShifts] = useState([]);
  const [selectedShift, setSelectedShift] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  
  // Ø­Ø§Ù„Ø© Ø§Ù„Ù†ÙˆØ§ÙØ°
  const [showRentalDetailsModal, setShowRentalDetailsModal] = useState(false);
  const [selectedRental, setSelectedRental] = useState(null);
  const [showShiftStatsModal, setShowShiftStatsModal] = useState(false);
  
  // Ø§Ù„ÙÙ„Ø§ØªØ±
  const [filters, setFilters] = useState({
    status: 'all',
    payment_status: 'all',
    dateFrom: '',
    dateTo: ''
  });
  
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [stats, setStats] = useState({
    totalRentals: 0,
    activeRentals: 0,
    completedRentals: 0,
    cancelledRentals: 0,
    totalRevenue: 0,
    todayRevenue: 0
  });

  // ==================== Ø¯ÙˆØ§Ù„ Ù…Ø³Ø§Ø¹Ø¯Ø© Ø«Ø§Ø¨ØªØ© (Ø®Ø§Ø±Ø¬ Ø§Ù„Ù…ÙƒÙˆÙ†) ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return num.toLocaleString('ar-EG') + ' Ø¬.Ù…';
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG');
    } catch {
      return '-';
    }
  };

  const getStatusBadge = (status) => {
    switch(status) {
      case 'Ù†Ø´Ø·':
        return <span className="status-badge active">ðŸ”¥ Ù†Ø´Ø·</span>;
      case 'Ù…ÙƒØªÙ…Ù„':
        return <span className="status-badge completed">âœ… Ù…ÙƒØªÙ…Ù„</span>;
      case 'Ù…Ù„ØºÙŠ':
        return <span className="status-badge cancelled">âŒ Ù…Ù„ØºÙŠ</span>;
      default:
        return <span className="status-badge">{status || 'ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ'}</span>;
    }
  };

  const getPaymentStatusBadge = (status) => {
    switch(status) {
      case 'Ù…Ø¯ÙÙˆØ¹':
        return <span className="payment-badge paid">ðŸ’° Ù…Ø¯ÙÙˆØ¹</span>;
      case 'ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹':
        return <span className="payment-badge unpaid">âš ï¸ ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹</span>;
      case 'Ø¬Ø²Ø¦ÙŠ':
        return <span className="payment-badge partial">ðŸ“Š Ø¬Ø²Ø¦ÙŠ</span>;
      case 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡':
        return <span className="payment-badge pending">â³ Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡</span>;
      default:
        return <span className="payment-badge">{status || 'ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙ'}</span>;
    }
  };

  // ==================== Ø¯ÙˆØ§Ù„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ØªØ¬Ø±ÙŠØ¨ÙŠØ© ====================
  const generateMockData = () => {
    console.log('ðŸ“± Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø¨ÙŠØ§Ù†Ø§Øª ØªØ¬Ø±ÙŠØ¨ÙŠØ©');
    
    const mockRentals = [];
    const employees = ['Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…Ø¯', 'Ù…Ø­Ù…ÙˆØ¯ Ø¹Ù„ÙŠ', 'Ø®Ø§Ù„Ø¯ Ø­Ø³Ù†'];
    const games = ['Ø³ÙŠØ§Ø±Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', 'Ø³ÙƒÙˆØªØ±', 'Ø¯Ø±Ø§Ø¬Ø© Ù†Ø§Ø±ÙŠØ©'];
    const customers = ['Ù…Ø­Ù…Ø¯ Ø£Ø­Ù…Ø¯', 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…ÙˆØ¯', 'Ø¹Ù„ÙŠ Ø­Ø³Ù†'];
    
    for (let i = 1; i <= 25; i++) {
      const now = new Date();
      now.setDate(now.getDate() - Math.floor(Math.random() * 10));
      
      const statusRand = Math.random();
      let rentalStatus = 'Ù…ÙƒØªÙ…Ù„';
      if (statusRand < 0.2) rentalStatus = 'Ù†Ø´Ø·';
      else if (statusRand < 0.3) rentalStatus = 'Ù…Ù„ØºÙŠ';
      
      const amount = 50 + Math.floor(Math.random() * 200);
      
      mockRentals.push({
        id: i,
        rental_number: `RENT-${i.toString().padStart(4, '0')}`,
        game_name: games[i % games.length],
        customer_name: customers[i % customers.length],
        customer_phone: `010${Math.floor(10000000 + Math.random() * 90000000)}`,
        employee_name: employees[i % employees.length],
