import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [monthlyRevenue, setMonthlyRevenue] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª ÙƒÙ„ Ø«Ø§Ù†ÙŠØ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
    const dataInterval = setInterval(loadDashboardData, 60000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      
      // Ø¬Ù„Ø¨ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙÙŠ ÙˆÙ‚Øª ÙˆØ§Ø­Ø¯
      const [
        statsResponse,
        branchesResponse,
        gamesResponse,
        rentalsResponse,
        usersResponse
      ] = await Promise.allSettled([
        analyticsService.getDashboardStats(),
        api.getBranches(),
        api.getGames(),
        api.getRentals({ status: 'Ù†Ø´Ø·', limit: 5 }),
        api.getUsers()
      ]);
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        setBranches(branchesResponse.value.data || []);
      } else {
        setBranches([
          { id: 1, name: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', location: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©', total_games: 10 },
          { id: 2, name: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', location: 'Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', total_games: 8 },
          { id: 3, name: 'ÙØ±Ø¹ Ø§Ù„ØªØ¬Ù…Ø¹', location: 'Ø§Ù„ØªØ¬Ù…Ø¹ Ø§Ù„Ø®Ø§Ù…Ø³', total_games: 12 }
        ]);
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
      if (statsResponse.status === 'fulfilled' && statsResponse.value.success) {
        const statsData = statsResponse.value.data?.summary || {};
        setStats({
          totalRevenue: statsData.total_revenue || 15000,
          totalRentals: statsData.total_rentals || 45,
          activeRentals: statsData.active_rentals || 3,
          availableGames: statsData.available_games || 15,
          todayRevenue: statsData.today_revenue || 1200,
          todayRentals: statsData.today_rentals || 2,
          totalUsers: statsData.total_users || 8,
          totalBranches: branches.length || 3,
          totalGames: statsData.total_games || 20,
          totalCustomers: statsData.total_customers || 25
        });
      } else {
        setStats({
          totalRevenue: 15000,
          totalRentals: 45,
          activeRentals: 3,
          availableGames: 15,
          todayRevenue: 1200,
          todayRentals: 2,
          totalUsers: 8,
          totalBranches: branches.length || 3,
          totalGames: 20,
          totalCustomers: 25
        });
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const gamesData = gamesResponse.value.data || [];
        setGames(gamesData);
        setStats(prev => ({
          ...prev,
          totalGames: gamesData.length,
          availableGames: gamesData.filter(g => g.status === 'Ù…ØªØ§Ø­').length
        }));
        calculateTopGames(gamesData);
