// src/services/authService.js - ÿßŸÑŸÜÿ≥ÿÆÿ© ÿßŸÑŸÖÿ≠ÿØÿ´ÿ© ÿßŸÑŸÉÿßŸÖŸÑÿ©
import api from './api';

const authService = {
  // ================= AUTHENTICATION =================
  
  login: async (email, password) => {
