    { id: 4, name: 'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', category: 'Ø³ÙŠØ§Ø±Ø§Øª', suggestedPrice: 200, image: 'electric-car.jpg' },
    { id: 5, name: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', suggestedPrice: 70, image: 'electric-scooter.jpg' },
    { id: 6, name: 'Ù‡Ø§Ø±Ù„ÙŠ', category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', suggestedPrice: 250, image: 'harley.jpg' },
    { id: 7, name: 'Ø³ÙŠØ¬ÙˆØ§ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', suggestedPrice: 90, image: 'segway.jpg' }
  ];

  // Ø¬Ù„Ø¨ Ø¬Ù…ÙŠØ¹ Ø§Ù„ÙØ±ÙˆØ¹
  const loadBranches = async () => {
    try {
      setLoading(true);
      setError('');
      
