      console.error('Error loading branches:', error);
    } finally {
      setLoading(false);
    }
  };

// âœ… **Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹**
const loadBranchGames = async (branchId) => {
  try {
    console.log(`ğŸ”„ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ ${branchId}...`);
    setLoadingGames(true);
    setGamesError('');
    
    const response = await authService.getBranchGames(branchId);
    
    console.log(`ğŸ“¥ Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:`, response);
    
    if (response.success) {
      console.log(`âœ… ØªÙ… Ø¬Ù„Ø¨ ${response.data.length} Ù„Ø¹Ø¨Ø© Ù„Ù„ÙØ±Ø¹`);
      setBranchGames(response.data);
    } else {
      console.log(`âŒ ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:`, response);
      
      // Ø¹Ø±Ø¶ Ø±Ø³Ø§Ù„Ø© Ø§Ù„Ø®Ø·Ø£ Ø§Ù„Ù…ÙØµÙ„Ø©
      const errorMessage = response.message || 'ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹';
