// frontend/src/pages/employee/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import rentalService from '../../services/rentalService';
import authService from '../../services/authService';
import RentalTimer from '../../components/rental/RentalTimer';

const EmployeeDashboard = () => {
  const [stats, setStats] = useState({
    available_games: 0,
    active_rentals: 0,
    completed_rentals: 0,
    canceled_rentals: 0
  });
  
  const [activeRentals, setActiveRentals] = useState([]);
  const [availableGames, setAvailableGames] = useState([]);
  const [newRental, setNewRental] = useState({
    game_id: '',
    customer_name: '',
    customer_phone: '',
    duration_minutes: 30
  });

  const loadDashboardData = async () => {
    try {
      // تحميل الإحصائيات
      const statsRes = await rentalService.getEmployeeStats();
      if (statsRes.success) setStats(statsRes.data);

      // تحميل التأجيرات النشطة
      const activeRes = await rentalService.getActiveRentals();
      if (activeRes.success) setActiveRentals(activeRes.data);

      // تحميل الألعاب المتاحة
      const gamesRes = await rentalService.getAvailableGames();
      if (gamesRes.success) setAvailableGames(gamesRes.data);
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    }
  };

  const handleStartRental = async () => {
    if (!newRental.game_id || !newRental.customer_name) {
      alert('يرجى اختيار لعبة واسم العميل');
      return;
    }

    const response = await rentalService.startRental(newRental);
    if (response.success) {
      alert('تم بدء التأجير بنجاح');
      setNewRental({
        game_id: '',
        customer_name: '',
        customer_phone: '',
        duration_minutes: 30
      });
      loadDashboardData();
    } else {
      alert(response.error || 'حدث خطأ أثناء بدء التأجير');
    }
  };

  const handleCancelRental = async (rentalId) => {
    if (!window.confirm('هل تريد إلغاء هذا التأجير؟')) return;

    const reason = prompt('سبب الإلغاء (اختياري):');
    const response = await rentalService.cancelRental(rentalId, reason);
    if (response.success) {
      alert('تم الإلغاء بنجاح');
      loadDashboardData();
    } else {
      alert(response.error || 'حدث خطأ أثناء الإلغاء');
    }
  };

  const handleExtendRental = async (rentalId) => {
    if (!window.confirm('هل تريد تمديد التأجير 15 دقيقة إضافية؟')) return;

    const response = await rentalService.extendRental(rentalId);
    if (response.success) {
      alert('تم التمديد بنجاح');
      loadDashboardData();
    } else {
      alert(response.error || 'حدث خطأ أثناء التمديد');
    }
  };

  useEffect(() => {
    loadDashboardData();
    const interval = setInterval(loadDashboardData, 30000); // تحديث كل 30 ثانية
    return () => clearInterval(interval);
  }, []);

  const user = authService.getUser();

  return (
    <div className="employee-dashboard">
      {/* Header مع اللوجو */}
      <header className="dashboard-header" style={{
        display: 'flex',
        alignItems: 'center',
        padding: '1rem',
        backgroundColor: '#f8f9fa',
        borderBottom: '1px solid #dee2e6',
        marginBottom: '2rem'
      }}>
        <img 
          src="/images/logol3bty.jpg" 
          alt="L3BTY Logo" 
          style={{ height: '50px', marginRight: '1rem' }}
        />
        <div>
          <h2 style={{ margin: 0 }}>لوحة الموظف</h2>
          <p style={{ margin: 0, color: '#6c757d' }}>
            {user?.branch_name || 'الفرع غير محدد'}
          </p>
        </div>
      </header>

      {/* الإحصائيات */}
      <div className="stats-cards row" style={{ marginBottom: '2rem' }}>
        <div className="col-md-3 mb-3">
          <div className="card stat-card" style={{ textAlign: 'center', backgroundColor: '#e8f5e9' }}>
            <div className="card-body">
              <h5 style={{ color: '#388e3c' }}>الألعاب المتاحة</h5>
              <h2 style={{ color: '#2e7d32' }}>{stats.available_games}</h2>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card stat-card" style={{ textAlign: 'center', backgroundColor: '#e3f2fd' }}>
            <div className="card-body">
              <h5 style={{ color: '#1976d2' }}>التأجيرات النشطة</h5>
              <h2 style={{ color: '#1565c0' }}>{stats.active_rentals}</h2>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card stat-card" style={{ textAlign: 'center', backgroundColor: '#fff3e0' }}>
            <div className="card-body">
              <h5 style={{ color: '#f57c00' }}>المكتملة</h5>
              <h2 style={{ color: '#ef6c00' }}>{stats.completed_rentals}</h2>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card stat-card" style={{ textAlign: 'center', backgroundColor: '#ffebee' }}>
            <div className="card-body">
              <h5 style={{ color: '#d32f2f' }}>الملغاة</h5>
              <h2 style={{ color: '#c62828' }}>{stats.canceled_rentals}</h2>
            </div>
          </div>
        </div>
      </div>

      {/* بدء تأجير جديد */}
      <div className="card mt-4" style={{ padding: '1.5rem', marginBottom: '2rem' }}>
        <h4 style={{ marginBottom: '1.5rem', color: '#333' }}>بدء تأجير جديد</h4>
        <div className="row g-3">
          <div className="col-md-3">
            <label className="form-label">اختر لعبة</label>
            <select 
              className="form-control"
              value={newRental.game_id}
              onChange={(e) => setNewRental({...newRental, game_id: e.target.value})}
              style={{ padding: '0.5rem' }}
            >
              <option value="">اختر لعبة...</option>
              {availableGames.map(game => (
                <option key={game.id || game._id} value={game.id || game._id}>
                  {game.name} - {game.price_per_hour || 0} ريال/ساعة
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label className="form-label">اسم العميل</label>
            <input
              type="text"
              className="form-control"
              placeholder="أدخل اسم العميل"
              value={newRental.customer_name}
              onChange={(e) => setNewRental({...newRental, customer_name: e.target.value})}
              style={{ padding: '0.5rem' }}
            />
          </div>
          <div className="col-md-3">
            <label className="form-label">رقم الهاتف</label>
            <input
              type="text"
              className="form-control"
              placeholder="أدخل رقم الهاتف"
              value={newRental.customer_phone}
              onChange={(e) => setNewRental({...newRental, customer_phone: e.target.value})}
              style={{ padding: '0.5rem' }}
            />
          </div>
          <div className="col-md-2">
            <label className="form-label">المدة (دقيقة)</label>
            <select 
              className="form-control"
              value={newRental.duration_minutes}
              onChange={(e) => setNewRental({...newRental, duration_minutes: parseInt(e.target.value)})}
              style={{ padding: '0.5rem' }}
            >
              <option value="15">15 دقيقة</option>
              <option value="30">30 دقيقة</option>
              <option value="60">60 دقيقة</option>
              <option value="90">90 دقيقة</option>
              <option value="120">120 دقيقة</option>
            </select>
          </div>
          <div className="col-md-1 d-flex align-items-end">
            <button 
              className="btn btn-success w-100"
              onClick={handleStartRental}
              style={{ padding: '0.5rem 1rem' }}
            >
              بدء
            </button>
          </div>
        </div>
      </div>

      {/* التأجيرات النشطة */}
      <div className="mt-4">
        <h4 style={{ marginBottom: '1rem', color: '#333' }}>التأجيرات النشطة</h4>
        {activeRentals.length === 0 ? (
          <div className="alert alert-info">
            لا توجد تأجيرات نشطة حالياً
          </div>
        ) : (
          <div className="row">
            {activeRentals.map((rental, index) => (
              <div key={rental.id || index} className="col-md-6 mb-3">
                <div className="card" style={{ borderLeft: '4px solid #1976d2' }}>
                  <div className="card-body">
                    <h5 style={{ color: '#1976d2' }}>{rental.customer_name}</h5>
                    <p style={{ marginBottom: '0.5rem' }}>
                      <strong>اللعبة:</strong> {rental.Game?.name || rental.game_name}
                    </p>
                    <p style={{ marginBottom: '0.5rem' }}>
                      <strong>الهاتف:</strong> {rental.customer_phone}
                    </p>
                    <p style={{ marginBottom: '1rem' }}>
                      <strong>المدة:</strong> {rental.duration_minutes} دقيقة
                    </p>
                    
                    {rental.expires_at && (
                      <RentalTimer 
                        rentalId={rental.id}
                        expiresAt={rental.expires_at}
                        onExpire={() => {
                          alert(`انتهى وقت تأجير ${rental.customer_name}`);
                          loadDashboardData();
                        }}
                      />
                    )}
                    
                    <div className="d-flex gap-2 mt-3">
                      <button 
                        className="btn btn-warning btn-sm"
                        onClick={() => handleExtendRental(rental.id)}
                      >
                        تمديد 15 دقيقة
                      </button>
                      <button 
                        className="btn btn-danger btn-sm"
                        onClick={() => handleCancelRental(rental.id)}
                      >
                        إلغاء
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

// تأكد من التصدير كـ default
export default EmployeeDashboard;
