      prevGames.map(game => 
        game.id === selectedGameId 
          ? { 
              ...game, 
              status: 'مؤجرة',
              rented_locally: true,
              rented_at: new Date().toISOString()
            } 
          : game
      )
    );

    const rentalData = {
      rental_number: `RENT-${Date.now()}-${Math.floor(Math.random() * 1000)}-${selectedGameId}`,
      game_id: selectedGameId,
      customer_name: rental.customer_name,
      customer_phone: rental.customer_phone,
      branch_id: user.branch_id,
      shift_id: currentShift.id,
      duration_minutes: durationMinutes,
      total_amount: totalAmount,
      payment_method: 'كاش',
      payment_status: isFixedTime ? 'مدفوع' : 'عند الإنهاء',
      paid_amount: isFixedTime ? totalAmount : 0,
      notes: `إعادة تأجير - التأجير السابق: ${rental.rental_number}`,
      status: 'نشط',
      start_time: new Date().toISOString()
    };

    const response = await api.post('/rentals/new', rentalData);

    if (response.success) {
      const newRental = {
        id: response.data?.id || response.rental_id || Date.now() + Math.random(),
        rental_number: response.data?.rental_number || response.rental_number || rentalData.rental_number,
        game_id: selectedGameId,
        game_name: selectedGameName,
        customer_name: rental.customer_name,
        customer_phone: rental.customer_phone,
        start_time: new Date().toISOString(),
        duration_minutes: durationMinutes,
        total_amount: totalAmount,
        payment_status: isFixedTime ? 'مدفوع' : 'عند الإنهاء',
        payment_method: 'كاش',
        status: 'نشط',
        time_left: isFixedTime ? `${durationMinutes}:00` : 'مفتوح',
        is_open_time: !isFixedTime,
        price_per_15min: selectedGamePrice,
        shift_id: currentShift.id,
        branch_id: user.branch_id
      };

      setActiveRentals(prev => [...prev, newRental]);
      
      setStatistics(prev => ({
        ...prev,
        activeRentals: prev.activeRentals + 1,
        availableGames: Math.max(0, prev.availableGames - 1)
      }));

      if (isFixedTime && totalAmount > 0) {
        await updateShiftRevenue(totalAmount);
      }

      showNotification('success', `
✅ تم إعادة التأجير بنجاح!

👤 العميل: ${rental.customer_name}
🎮 اللعبة: ${selectedGameName}
⏱️ ${isFixedTime ? `${durationMinutes} دقيقة` : 'وقت مفتوح'}
💰 ${isFixedTime ? `المبلغ: ${formatCurrency(totalAmount)}` : 'يدفع عند الإنهاء'}
      `);

      setTimeout(async () => {
        await loadActiveRentals();
        await updateStatistics();
      }, 1000);

    } else {
      throw new Error(response.message || 'فشل إنشاء التأجير');
    }

  } catch (error) {
    console.error('❌ خطأ في إعادة التأجير:', error);
    
    let errorMessage = '❌ فشل إعادة التأجير';
    
    if (error.message.includes('timeout')) {
      errorMessage += '\n⏱️ انتهت مهلة الاتصال';
    } else if (error.message.includes('network')) {
      errorMessage += '\n🌐 مشكلة في الاتصال';
    } else {
      errorMessage += `\n🔧 ${error.message}`;
    }
    
    showNotification('error', errorMessage);
    
    if (rental && rental.game_id) {
      setGames(prevGames => 
        prevGames.map(game => 
