// frontend/src/pages/admin/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import service from '../../services/service';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  const [recentRentals, setRecentRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const currentUser = service.auth.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      const statsResponse = await service.getDashboardStats();
      if (statsResponse.success) {
        setStats(statsResponse.data);
      }

      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ¢ÿÆÿ± ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
      const rentalsResponse = await service.getRecentRentals();
      if (rentalsResponse.success) {
        setRecentRentals(rentalsResponse.data);
      }

    } catch (error) {
      console.error('Error loading dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 2
    }).format(amount || 0);
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* Header */}
      <div className="dashboard-header">
        <div>
          <h1>ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ•ÿØÿßÿ±Ÿäÿ©</h1>
          <p>ŸÖÿ±ÿ≠ÿ®ÿßŸã {user?.name} | ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´: {new Date().toLocaleTimeString('ar-EG')}</p>
        </div>
        <button onClick={loadDashboardData} className="btn-refresh">
          üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
        </button>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card revenue">
          <div className="stat-icon">üí∞</div>
          <div className="stat-content">
            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™</h3>
            <h2>{formatCurrency(stats.totalRevenue)}</h2>
          </div>
        </div>

        <div className="stat-card rentals">
          <div className="stat-icon">‚è±Ô∏è</div>
          <div className="stat-content">
            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</h3>
            <h2>{stats.totalRentals}</h2>
          </div>
        </div>

        <div className="stat-card active">
          <div className="stat-icon">üöÄ</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h3>
            <h2>{stats.activeRentals}</h2>
          </div>
        </div>

        <div className="stat-card games">
          <div className="stat-icon">üéÆ</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</h3>
            <h2>{stats.availableGames}</h2>
          </div>
        </div>
      </div>

      {/* Today's Stats */}
      <div className="today-section">
        <h2>üìä ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸäŸàŸÖ</h2>
        <div className="today-grid">
          <div className="today-card">
            <h3>ÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖ</h3>
            <h2>{formatCurrency(stats.todayRevenue)}</h2>
          </div>
          <div className="today-card">
            <h3>ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸäŸàŸÖ</h3>
            <h2>{stats.todayRentals}</h2>
          </div>
          <div className="today-card">
            <h3>ŸÖÿ™Ÿàÿ≥ÿ∑ ÿßŸÑŸÇŸäŸÖÿ©</h3>
            <h2>{stats.todayRentals > 0 ? formatCurrency(stats.todayRevenue / stats.todayRentals) : formatCurrency(0)}</h2>
          </div>
        </div>
      </div>

      {/* Recent Rentals */}
      <div className="recent-section">
        <div className="section-header">
          <h2>üïí ÿ¢ÿÆÿ± ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</h2>
          <Link to="/employee/rentals" className="view-all">
            ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ ‚Üí
          </Link>
        </div>
        
        {recentRentals.length === 0 ? (
          <div className="empty-state">
            <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿ≠ÿßŸÑŸäÿßŸã</p>
          </div>
        ) : (
          <div className="rentals-table">
            <table>
              <thead>
                <tr>
                  <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                  <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                  <th>ÿßŸÑŸÅÿ±ÿπ</th>
                  <th>ÿßŸÑŸÖÿ®ŸÑÿ∫</th>
                  <th>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
                  <th>ÿßŸÑŸàŸÇÿ™</th>
                </tr>
              </thead>
              <tbody>
                {recentRentals.map(rental => (
                  <tr key={rental.id}>
                    <td>{rental.customer_name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}</td>
                    <td>{rental.game_name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}</td>
                    <td>{rental.branch_name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}</td>
                    <td className="amount">{formatCurrency(rental.total_amount)}</td>
                    <td>
                      <span className={`status-badge ${rental.status === 'ŸÜÿ¥ÿ∑' ? 'active' : 'completed'}`}>
                        {rental.status || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}
                      </span>
                    </td>
                    <td className="time">{formatTime(rental.created_at)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h2>‚ö° ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ©</h2>
        <div className="actions-grid">
          <Link to="/admin/users" className="action-card">
            <div className="action-icon">üë•</div>
            <div className="action-text">
              <h4>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ</h4>
              <p>ÿ•ÿ∂ÿßŸÅÿ© Ÿàÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ</p>
            </div>
          </Link>
          
          <Link to="/admin/games" className="action-card">
            <div className="action-icon">üéÆ</div>
            <div className="action-text">
              <h4>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®</h4>
              <p>ÿ•ÿ∂ÿßŸÅÿ© Ÿàÿ™ÿπÿØŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
            </div>
          </Link>
          
          <Link to="/admin/branches" className="action-card">
            <div className="action-icon">üè¨</div>
            <div className="action-text">
              <h4>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÅÿ±Ÿàÿπ</h4>
              <p>ÿ™ÿπÿØŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ</p>
            </div>
          </Link>
          
          <Link to="/employee/rentals" className="action-card">
            <div className="action-icon">‚è±Ô∏è</div>
            <div className="action-text">
              <h4>ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ</h4>
              <p>ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ŸÑÿπÿ®ÿ© ŸÑŸÑÿπŸÖŸäŸÑ</p>
            </div>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
