// frontend/src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState(null);
  
  // بيانات الألعاب
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // تأجير متعدد
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [showRentalForm, setShowRentalForm] = useState(false);
  
  // بيانات النموذج
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [discount, setDiscount] = useState(0);
  
  const timersRef = useRef({});

  // تأثيرات
  useEffect(() => {
    if (user) {
      initializeShift();
      loadData();
      
      const interval = setInterval(() => {
        if (currentShift) {
          loadData();
        }
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user, currentShift]);

  // ==================== SHIFT MANAGEMENT ====================


const initializeShift = async () => {
  try {
    const response = await api.get('/shifts/active');
    
    // ✅ التحقق من وجود الاستجابة أولاً
    if (response && response.data) {
      if (response.data.success) {
        setCurrentShift(response.data.data);
        if (response.data.data) {
          loadShiftStats(response.data.data.id);
        }
      } else {
        // ✅ التعامل مع حالة عدم النجاح
        console.log('No active shift:', response.data.message);
        setCurrentShift(null);
      }
    } else {
      // ✅ التعامل مع استجابة null
      console.error('Invalid response from server');
      setCurrentShift(null);
    }
  } catch (error) {
    console.error('Error fetching active shift:', error);
    // ✅ إعادة تعيين الحالة عند وجود خطأ
    setCurrentShift(null);
    
    // ✅ إظهار رسالة خطأ للمستخدم
    if (error.response?.status === 401) {
      showNotification('error', 'انتهت جلستك. يرجى تسجيل الدخول مرة أخرى');
    } else {
      showNotification('error', 'فشل في تحميل بيانات الشيفت');
    }
  } finally {
    setLoading(false);
  }
};

  const startNewShift = async () => {
    try {
      const response = await api.post('/shifts/start');
      if (response.data.success) {
        showNotification('success', 'تم بدء الشيفت بنجاح');
        await initializeShift();
        await loadData();
      }
    } catch (error) {
      showNotification('error', error.response?.data?.message || 'فشل في بدء الشيفت');
    }
  };

  const endCurrentShift = async () => {
    if (!currentShift) return;
    
    if (!window.confirm('هل تريد إنهاء الشيفت الحالي؟ سيتم إخفاء جميع البيانات وبدء يوم جديد.')) {
      return;
    }
    
    try {
      const response = await api.post(`/shifts/${currentShift.id}/end`);
      if (response.data.success) {
        showNotification('success', `تم إنهاء الشيفت بنجاح! الإيرادات: ${formatCurrency(response.data.stats.total_revenue)}`);
        
        // إعادة تعيين كل شيء
        setCurrentShift(null);
        setShiftStats(null);
        setAvailableGames([]);
        setActiveRentals([]);
        setSelectedGames([]);
      }
    } catch (error) {
      showNotification('error', error.response?.data?.message || 'فشل في إنهاء الشيفت');
    }
  };

  const loadShiftStats = async (shiftId) => {
    try {
      const response = await api.get(`/shifts/${shiftId}/stats`);
      if (response.data.success) {
        setShiftStats(response.data.data);
      }
    } catch (error) {
      console.error('Error loading shift stats:', error);
    }
  };

  // ==================== LOAD DATA ====================

  const loadData = async () => {
    try {
      if (!currentShift) return;
      
      await Promise.all([
        loadAvailableGames(),
        loadActiveRentals(),
        loadShiftStats(currentShift.id)
      ]);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const loadAvailableGames = async () => {
    try {
      const response = await api.get('/games', {
        params: {
          branch_id: user.branch_id,
          status: 'متاح'
        }
      });
      
      if (response.data.success) {
        setAvailableGames(response.data.data);
      }
    } catch (error) {
      console.error('Error loading available games:', error);
    }
  };

  const loadActiveRentals = async () => {
    try {
      const response = await api.get('/rentals', {
        params: {
          branch_id: user.branch_id,
          status: 'نشط',
          shift_id: currentShift?.id
        }
      });
      
      if (response.data.success) {
        const rentals = response.data.data;
        setActiveRentals(rentals);
        
        // تنظيف المؤقتات القديمة
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        timersRef.current = {};
        
        // إنشاء مؤقتات جديدة
        rentals.forEach(rental => {
          startTimerForRental(rental);
        });
      }
    } catch (error) {
      console.error('Error loading active rentals:', error);
    }
  };

  const startTimerForRental = (rental) => {
    if (!rental.expected_end_time || rental.duration_minutes === 0) return;
    
    const endTime = new Date(rental.expected_end_time);
    const now = new Date();
    const timeLeft = endTime - now;
    
    if (timeLeft <= 0) {
      completeExpiredRental(rental.id);
      return;
    }
    
    const timerId = setInterval(() => {
      const now = new Date();
      const timeLeft = endTime - now;
      
      if (timeLeft <= 0) {
        clearInterval(timersRef.current[rental.id]);
        delete timersRef.current[rental.id];
        completeExpiredRental(rental.id);
      }
    }, 60000);
    
    timersRef.current[rental.id] = timerId;
  };

  // ==================== MULTI-GAME RENTAL ====================

  const addGameToRental = (game) => {
    const existingIndex = selectedGames.findIndex(g => g.id === game.id);
    
    if (existingIndex === -1) {
      // إضافة لعبة جديدة
      const maxQuantity = Math.min(game.quantity || 1, 5);
      setSelectedGames([...selectedGames, { ...game, rentalId: Date.now() }]);
      setQuantities({ ...quantities, [game.id]: 1 });
    } else {
      // زيادة كمية لعبة موجودة
      const currentQuantity = quantities[game.id] || 1;
      const maxQuantity = Math.min(game.quantity || 1, 5);
      
      if (currentQuantity < maxQuantity) {
        setQuantities({ ...quantities, [game.id]: currentQuantity + 1 });
      }
    }
    
    // فتح نموذج التأجير تلقائياً إذا لم يكن مفتوحاً
    if (!showRentalForm) {
      setShowRentalForm(true);
    }
  };

  const removeGameFromRental = (gameId) => {
    const currentQuantity = quantities[gameId] || 1;
    
    if (currentQuantity > 1) {
      setQuantities({ ...quantities, [gameId]: currentQuantity - 1 });
    } else {
      setSelectedGames(selectedGames.filter(game => game.id !== gameId));
      const newQuantities = { ...quantities };
      delete newQuantities[gameId];
      setQuantities(newQuantities);
    }
  };

  const calculateTotalPrice = () => {
    let total = 0;
    
    selectedGames.forEach(game => {
      const quantity = quantities[game.id] || 1;
      let gamePrice = 0;
      
      if (selectedDuration >= 60) {
        gamePrice = (game.price_per_hour || 0) * (selectedDuration / 60);
      } else {
        const quarters = Math.ceil(selectedDuration / 15);
        gamePrice = (game.price_per_15min || 0) * quarters;
      }
      
      total += gamePrice * quantity;
    });
    
    return total * (1 - discount / 100);
  };

  const handleStartMultiRental = async () => {
    if (!validateForm()) return;
    if (!currentShift) {
      showNotification('error', 'يجب بدء شيفت أولاً');
      return;
    }
    
    try {
      const rentalGames = selectedGames.map(game => ({
        game_id: game.id,
        quantity: quantities[game.id] || 1,
        duration_minutes: selectedDuration
      }));
      
      const rentalData = {
        games: rentalGames,
        customer_name: customerName.trim(),
        customer_phone: customerPhone.trim(),
        shift_id: currentShift.id,
        payment_method: paymentMethod,
        notes: customerNotes,
        discount: discount
      };
      
      const response = await api.post('/rentals/multi', rentalData);
      
      if (response.data.success) {
        showNotification('success', response.data.message);
        
        // إعادة تحميل البيانات
        await loadAvailableGames();
        await loadActiveRentals();
        await loadShiftStats(currentShift.id);
        
        // إعادة تعيين النموذج
        resetForm();
        setShowRentalForm(false);
      }
    } catch (error) {
      showNotification('error', error.response?.data?.message || 'حدث خطأ في بدء التأجير');
    }
  };

  // ==================== RENTAL OPERATIONS ====================

  const completeExpiredRental = async (rentalId) => {
    try {
      const response = await api.put(`/rentals/${rentalId}/complete`);
      if (response.data.success) {
        console.log(`Rental ${rentalId} completed automatically`);
        await loadActiveRentals();
        await loadAvailableGames();
        if (currentShift) {
          await loadShiftStats(currentShift.id);
        }
      }
    } catch (error) {
      console.error('Error completing rental:', error);
    }
  };

  const handleEndRental = async (rentalId) => {
    try {
      const rental = activeRentals.find(r => r.id === rentalId);
      if (!rental) return;
      
      if (rental.duration_minutes === 0) {
        const finalAmount = prompt('أدخل السعر النهائي:', rental.total_amount);
        if (!finalAmount || isNaN(finalAmount)) {
          showNotification('error', 'السعر غير صحيح');
          return;
        }
        
        await api.put(`/rentals/${rentalId}/complete`, {
          final_amount: parseFloat(finalAmount)
        });
      } else {
        if (!window.confirm(`هل تريد إنهاء تأجير #${rental.rental_number}؟`)) return;
        
        await api.put(`/rentals/${rentalId}/complete`);
      }
      
      showNotification('success', `تم إنهاء التأجير بنجاح`);
      await loadActiveRentals();
      await loadAvailableGames();
      if (currentShift) {
        await loadShiftStats(currentShift.id);
      }
    } catch (error) {
      showNotification('error', 'حدث خطأ في إنهاء التأجير');
    }
  };

  // ==================== HELPER FUNCTIONS ====================

  const validateForm = () => {
    if (!customerName.trim()) {
      showNotification('error', 'الرجاء إدخال اسم العميل');
      return false;
    }
    
    if (!customerPhone.trim()) {
      showNotification('error', 'الرجاء إدخال رقم الهاتف');
      return false;
    }
    
    if (!/^01[0-9]{9}$/.test(customerPhone)) {
      showNotification('error', 'رقم الهاتف غير صحيح');
      return false;
    }
    
    if (selectedGames.length === 0) {
      showNotification('error', 'الرجاء اختيار لعبة واحدة على الأقل');
      return false;
    }
    
    return true;
  };

  const resetForm = () => {
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setQuantities({});
    setDurationType('fixed');
    setSelectedDuration(60);
    setPaymentMethod('cash');
    setDiscount(0);
  };

  const showNotification = (type, message) => {
    const types = {
      error: '❌',
      success: '✅',
      warning: '⚠️',
      info: 'ℹ️'
    };
    alert(`${types[type]} ${message}`);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount || 0);
  };

  const formatDuration = (minutes) => {
    if (!minutes || minutes === 0) return 'وقت مفتوح';
    if (minutes < 60) return `${minutes} دقيقة`;
    
    const hours = Math.floor(minutes / 60);
    const remaining = minutes % 60;
    return `${hours} ساعة ${remaining > 0 ? remaining + ' دقيقة' : ''}`;
  };

  const calculateTimeRemaining = (rental) => {
    if (!rental.expected_end_time) return null;
    
    const endTime = new Date(rental.expected_end_time);
    const now = new Date();
    const diff = endTime - now;
    
    if (diff <= 0) return { expired: true, text: 'انتهى' };
    
    const minutes = Math.floor(diff / 60000);
    if (minutes < 60) return { expired: false, text: `${minutes} دقيقة` };
    
    const hours = Math.floor(minutes / 60);
    const remaining = minutes % 60;
    return { expired: false, text: `${hours} ساعة ${remaining} دقيقة` };
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل البيانات...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* 🔧 قسم إدارة الشيفت */}
      <div className="shift-section">
        {currentShift ? (
          <div className="active-shift">
            <div className="shift-header">
              <h2>⏱️ شيفت نشط</h2>
              <div className="shift-time">
                <span>بدأ: {new Date(currentShift.start_time).toLocaleTimeString('ar-EG')}</span>
                <span>المدة: {Math.floor((Date.now() - new Date(currentShift.start_time)) / 60000)} دقيقة</span>
              </div>
            </div>
            
            <div className="shift-stats">
              {shiftStats && (
                <>
                  <div className="stat-item">
                    <span className="stat-label">التأجيرات:</span>
                    <span className="stat-value">{shiftStats.total_rentals || 0}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">الإيرادات:</span>
                    <span className="stat-value">{formatCurrency(shiftStats.total_revenue || 0)}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">العملاء:</span>
                    <span className="stat-value">{shiftStats.unique_customers || 0}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">النشطة:</span>
                    <span className="stat-value">{activeRentals.length}</span>
                  </div>
                </>
              )}
            </div>
            
            <button 
              className="btn-end-shift"
              onClick={endCurrentShift}
            >
              🏁 إنهاء الشيفت
            </button>
          </div>
        ) : (
          <div className="no-shift">
            <h3>📭 لا يوجد شيفت نشط</h3>
            <p>يجب بدء شيفت جديد للبدأ في العمل</p>
            <button 
              className="btn-start-shift"
              onClick={startNewShift}
            >
              🚀 بدء شيفت جديد
            </button>
          </div>
        )}
      </div>

      {/* 🎮 الألعاب المتاحة */}
      <div className="games-section">
        <div className="section-header">
          <h2>🎮 الألعاب المتاحة ({availableGames.length})</h2>
          <div className="selected-games-info">
            {selectedGames.length > 0 && (
              <button 
                className="btn-start-rental"
                onClick={() => setShowRentalForm(true)}
              >
                🚀 تأجير ({selectedGames.length})
              </button>
            )}
          </div>
        </div>
        
        <div className="games-grid">
          {availableGames.map(game => (
            <div key={game.id} className="game-card">
              <div className="game-image">
                <img 
                  src={`/images/${game.image_url || 'default-game.jpg'}`}
                  alt={game.name}
                />
                <div className="game-status">
                  <span className={`status-badge ${game.status === 'متاح' ? 'available' : 'rented'}`}>
                    {game.status === 'متاح' ? '✅ متاح' : '⏱️ مؤجرة'}
                  </span>
                </div>
              </div>
              
              <div className="game-info">
                <h3>{game.name}</h3>
                <p className="game-category">{game.category}</p>
                
                <div className="game-pricing">
                  <div className="price-item">
                    <span>سعر الساعة:</span>
                    <span>{formatCurrency(game.price_per_hour)}</span>
                  </div>
                  <div className="price-item">
                    <span>سعر الربع:</span>
                    <span>{formatCurrency(game.price_per_15min)}</span>
                  </div>
                </div>
                
                <div className="game-quantity">
                  <span>الكمية المتاحة: {game.quantity || 1}</span>
                </div>
                
                <button 
                  className="btn-add-to-rental"
                  onClick={() => addGameToRental(game)}
                  disabled={!currentShift}
                >
                  + إضافة للتأجير
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ⏱️ التأجيرات النشطة */}
      <div className="active-rentals-section">
        <h2>⏱️ التأجيرات النشطة ({activeRentals.length})</h2>
        
        {activeRentals.length === 0 ? (
          <div className="empty-state">
            <p>📭 لا توجد تأجيرات نشطة حالياً</p>
          </div>
        ) : (
          <div className="rentals-grid">
            {activeRentals.map(rental => {
              const timeRemaining = calculateTimeRemaining(rental);
              
              return (
                <div key={rental.id} className="rental-card">
                  <div className="rental-header">
                    <h3>📋 تأجير #{rental.rental_number}</h3>
                    <span className="customer-name">👤 {rental.customer_name}</span>
                  </div>
                  
                  <div className="rental-info">
                    <div className="info-row">
                      <span className="label">اللعبة:</span>
                      <span className="value">{rental.game_name}</span>
                    </div>
                    <div className="info-row">
                      <span className="label">الكمية:</span>
                      <span className="value">{rental.quantity}</span>
                    </div>
                    <div className="info-row">
                      <span className="label">المدة:</span>
                      <span className="value">{formatDuration(rental.duration_minutes)}</span>
                    </div>
                    <div className="info-row">
                      <span className="label">المبلغ:</span>
                      <span className="value amount">{formatCurrency(rental.total_amount)}</span>
                    </div>
                    {timeRemaining && (
                      <div className="info-row">
                        <span className="label">المتبقي:</span>
                        <span className={`value ${timeRemaining.expired ? 'expired' : ''}`}>
                          {timeRemaining.text}
                        </span>
                      </div>
                    )}
                  </div>
                  
                  <div className="rental-actions">
                    <button 
                      className="btn-end-rental"
                      onClick={() => handleEndRental(rental.id)}
                    >
                      ✅ إنهاء
                    </button>
                    <button className="btn-extend">⏰ تمديد</button>
                    <button className="btn-details">📋 تفاصيل</button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* 🚀 نافذة تأجير جديدة */}
      {showRentalForm && (
        <div className="modal-overlay">
          <div className="modal rental-modal">
            <div className="modal-header">
              <h2>🚀 تأجير جديد</h2>
              <button 
                className="close-btn"
                onClick={() => {
                  setShowRentalForm(false);
                  resetForm();
                }}
              >
                ✕
              </button>
            </div>
            
            <div className="modal-body">
              {/* 👤 بيانات العميل */}
              <div className="form-section">
                <h3>👤 بيانات العميل</h3>
                <div className="form-row">
                  <div className="form-group">
                    <label>اسم العميل *</label>
                    <input
                      type="text"
                      value={customerName}
                      onChange={(e) => setCustomerName(e.target.value)}
                      placeholder="أدخل اسم العميل"
                    />
                  </div>
                  <div className="form-group">
                    <label>رقم الهاتف *</label>
                    <input
                      type="tel"
                      value={customerPhone}
                      onChange={(e) => setCustomerPhone(e.target.value)}
                      placeholder="01xxxxxxxxx"
                    />
                  </div>
                </div>
                <div className="form-group">
                  <label>ملاحظات</label>
                  <textarea
                    value={customerNotes}
                    onChange={(e) => setCustomerNotes(e.target.value)}
                    placeholder="ملاحظات إضافية..."
                    rows="2"
                  />
                </div>
              </div>
              
              {/* 🎮 الألعاب المختارة */}
              <div className="form-section">
                <h3>🎮 الألعاب المختارة ({selectedGames.length})</h3>
                <div className="selected-games-list">
                  {selectedGames.map(game => (
                    <div key={game.id} className="selected-game-item">
                      <div className="game-details">
                        <div className="game-image-small">
                          <img 
                            src={`/images/${game.image_url || 'default-game.jpg'}`}
                            alt={game.name}
                          />
                        </div>
                        <div>
                          <h4>{game.name}</h4>
                          <p>{game.category}</p>
                          <p className="price">{formatCurrency(game.price_per_hour)}/ساعة</p>
                        </div>
                      </div>
                      <div className="game-quantity-control">
                        <button 
                          className="quantity-btn"
                          onClick={() => removeGameFromRental(game.id)}
                        >
                          -
                        </button>
                        <span className="quantity">{quantities[game.id] || 1}</span>
                        <button 
                          className="quantity-btn"
                          onClick={() => addGameToRental(game)}
                        >
                          +
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
              
              {/* ⏱️ مدة التأجير */}
              <div className="form-section">
                <h3>⏱️ مدة التأجير</h3>
                <div className="duration-options">
                  <label className="duration-option">
                    <input
                      type="radio"
                      checked={durationType === 'fixed'}
                      onChange={() => setDurationType('fixed')}
                    />
                    <span>مدة ثابتة</span>
                  </label>
                  <label className="duration-option">
                    <input
                      type="radio"
                      checked={durationType === 'open'}
                      onChange={() => setDurationType('open')}
                    />
                    <span>وقت مفتوح</span>
                  </label>
                </div>
                
                {durationType === 'fixed' && (
                  <div className="fixed-duration">
                    <select
                      value={selectedDuration}
                      onChange={(e) => setSelectedDuration(parseInt(e.target.value))}
                      className="duration-select"
                    >
                      <option value="15">15 دقيقة</option>
                      <option value="30">30 دقيقة</option>
                      <option value="45">45 دقيقة</option>
                      <option value="60">1 ساعة</option>
                      <option value="120">2 ساعة</option>
                      <option value="180">3 ساعة</option>
                      <option value="240">4 ساعة</option>
                    </select>
                  </div>
                )}
                
                {durationType === 'open' && (
                  <div className="open-duration-notice">
                    ⏳ الوقت مفتوح - سيتم تحديد السعر عند إنهاء التأجير
                  </div>
                )}
              </div>
              
              {/* 💰 الخصم والدفع */}
              <div className="form-section">
                <h3>💰 الدفع</h3>
                <div className="pricing-summary">
                  <div className="price-item">
                    <span>المبلغ الإجمالي:</span>
                    <span>{formatCurrency(calculateTotalPrice())}</span>
                  </div>
                  
                  <div className="discount-section">
                    <label>الخصم:</label>
                    <div className="discount-options">
                      <button 
                        className={`discount-btn ${discount === 0 ? 'active' : ''}`}
                        onClick={() => setDiscount(0)}
                      >
                        بدون
                      </button>
                      <button 
                        className={`discount-btn ${discount === 10 ? 'active' : ''}`}
                        onClick={() => setDiscount(10)}
                      >
                        10%
                      </button>
                      <button 
                        className={`discount-btn ${discount === 20 ? 'active' : ''}`}
                        onClick={() => setDiscount(20)}
                      >
                        20%
                      </button>
                    </div>
                  </div>
                  
                  <div className="payment-methods">
                    <label>طريقة الدفع:</label>
                    <div className="payment-options">
                      <label>
                        <input
                          type="radio"
                          checked={paymentMethod === 'cash'}
                          onChange={() => setPaymentMethod('cash')}
                        />
                        <span>💵 كاش</span>
                      </label>
                      <label>
                        <input
                          type="radio"
                          checked={paymentMethod === 'card'}
                          onChange={() => setPaymentMethod('card')}
                        />
                        <span>💳 بطاقة</span>
                      </label>
                      <label>
                        <input
                          type="radio"
                          checked={paymentMethod === 'vodafone_cash'}
                          onChange={() => setPaymentMethod('vodafone_cash')}
                        />
                        <span>📱 فودافون كاش</span>
                      </label>
                    </div>
                  </div>
                </div>
              </div>
              
              {/* 🔘 أزرار الإجراءات */}
              <div className="modal-actions">
                <button 
                  className="btn-start"
                  onClick={handleStartMultiRental}
                  disabled={!validateForm() || selectedGames.length === 0}
                >
                  🚀 بدء التأجير
                </button>
                <button 
                  className="btn-cancel"
                  onClick={() => {
                    setShowRentalForm(false);
                    resetForm();
                  }}
                >
                  إلغاء
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Rentals;
