// src/services/authService.js
import api from './api';

const authService = {
  // ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿµÿßÿØŸÇÿ© ====================
  login: async (email, password) => {
    try {
      console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ...');
      const response = await api.login(email, password);
      
      if (response.success && response.data?.token) {
        const { token, user } = response.data;
        api.setToken(token);
        api.setUser(user);
        localStorage.setItem('lastLogin', new Date().toISOString());
        console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠');
      }
      
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ'
      };
    }
  },

  logout: api.logout,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ====================
  getCurrentUser: api.getUser,
  
  getToken: api.getToken,
  
  isAuthenticated: () => {
    const token = api.getToken();
    const user = api.getUser();
    return !!(token && user);
  },

  hasRole: (role) => {
    const user = api.getUser();
    return user?.role === role;
  },

  hasPermission: api.hasPermission,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑÿ®ÿ±ŸàŸÅÿßŸäŸÑ ====================
  getProfile: api.getProfile,
  
  updateProfile: async (profileData) => {
    const response = await api.updateProfile(profileData);
    if (response.success && response.data) {
      api.setUser(response.data);
    }
    return response;
  },

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™ ====================
  getActiveShift: api.getActiveShift,
  startShift: api.startShift,
  getShifts: api.getShifts,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ ====================
  getBranches: api.getBranches,
  getBranchStats: api.getBranchStats,
  getBranch: api.getBranch,
  getBranchGames: api.getBranchGames,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® ====================
  getGames: api.getGames,
  getAvailableGames: api.getAvailableGames,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ====================
  getRentals: api.getRentals,
  createRental: api.createRental,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ====================
  getUsers: api.getUsers,

  // ==================== ÿØŸàÿßŸÑ ÿ£ÿÆÿ±Ÿâ ====================
  healthCheck: api.healthCheck,

  // ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿßÿπÿØÿ© ====================
  formatCurrency: (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(num);
  },

  formatDuration: (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours} ÿ≥ÿßÿπÿ©${remainingMinutes > 0 ? ` ${remainingMinutes} ÿØŸÇŸäŸÇÿ©` : ''}`;
  },

  validateCustomerPhone: (phone) => {
    const phoneRegex = /^01[0-9]{9}$/;
    return phoneRegex.test(phone);
  },

  showNotification: (type, message) => {
    const messages = {
      error: `‚ùå ${message}`,
      success: `‚úÖ ${message}`,
      warning: `‚ö†Ô∏è ${message}`,
      info: `‚ÑπÔ∏è ${message}`
    };
    
    alert(messages[type] || message);
  },

  // ==================== ÿØŸàÿßŸÑ ÿÆÿßÿµÿ© ÿ®ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä ====================
  getCurrentUserBranch: async () => {
    try {
      const user = api.getUser();
      if (!user?.branch_id) {
        return {
          success: false,
          message: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÅÿ±ÿπ',
          data: null
        };
      }
      
      return await api.getBranch(user.branch_id);
    } catch (error) {
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ',
        data: null
      };
    }
  },

  getAvailableGamesForCurrentBranch: async () => {
    try {
      const user = api.getUser();
      if (!user?.branch_id) {
        return {
          success: false,
          message: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÅÿ±ÿπ',
          data: []
        };
      }
      
      return await api.getAvailableGames({ branch_id: user.branch_id });
    } catch (error) {
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ',
        data: []
      };
    }
  },

  refreshCurrentBranchData: async () => {
    try {
      const user = api.getUser();
      if (!user?.branch_id) return null;
      
      const [gamesResponse, branchResponse] = await Promise.all([
        authService.getAvailableGamesForCurrentBranch(),
        authService.getCurrentUserBranch()
      ]);
      
      return {
        success: true,
        games: gamesResponse.success ? gamesResponse.data : [],
        branch: branchResponse.success ? branchResponse.data : null,
        message: 'ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™'
      };
    } catch (error) {
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
        games: [],
        branch: null
      };
    }
  }
};

export default authService;
