import axios from 'axios';

// ==================== إعدادات أساسية ====================
// عدل baseURL ليكون بدون /api
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const api = axios.create({
  baseURL: API_BASE_URL, // http://localhost:5000 فقط
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  }
});

// ==================== Interceptors ====================
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    // تصحيح: إضافة مسار /api/ لجميع الطلبات
    if (!config.url.startsWith('/api/')) {
      config.url = `/api${config.url.startsWith('/') ? '' : '/'}${config.url}`;
    }
    
    return config;
  },
  (error) => Promise.reject(error)
);

api.interceptors.response.use(
  (response) => {
    const responseData = response.data;
    
    if (responseData && typeof responseData === 'object' && 'success' in responseData) {
      return responseData;
    }
    
    return {
      success: true,
      status: response.status,
      data: responseData,
      message: 'تم تنفيذ العملية بنجاح'
    };
  },
  (error) => {
    console.error('❌ خطأ في API:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.message,
      data: error.response?.data
    });
    
    let apiError = {
      success: false,
      status: error.response?.status || 0,
      message: '',
      data: error.response?.data,
      error: error
    };
    
    if (error.code === 'ERR_NETWORK') {
      apiError.message = 'تعذر الاتصال بالخادم. تأكد من تشغيل الخادم على http://localhost:5000';
    } else if (error.response?.status === 401) {
      apiError.message = 'انتهت جلسة العمل. يرجى تسجيل الدخول مرة أخرى';
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      localStorage.removeItem('lastLogin');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    } else if (error.response?.status === 404) {
      apiError.message = 'المسار غير موجود: ' + error.config?.url;
    } else if (error.response?.status === 500) {
      apiError.message = 'حدث خطأ في الخادم';
    } else {
      apiError.message = error.response?.data?.message || error.message || 'حدث خطأ غير معروف';
    }
    
    return Promise.reject(apiError);
  }
);

// ==================== دوال المساعدة ====================
api.setToken = (token) => {
  if (token) {
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    localStorage.setItem('token', token);
  } else {
    delete api.defaults.headers.common['Authorization'];
    localStorage.removeItem('token');
  }
};

api.checkAuthStatus = () => {
  const token = localStorage.getItem('token');
  const user = localStorage.getItem('user');
  
  return {
    isAuthenticated: !!token,
    token: token,
    user: user ? JSON.parse(user) : null
  };
};

// ==================== المصادقة ====================
api.login = async (email, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/api/auth/login`, {
      email, 
      password
    }, {
      headers: {
        'Content-Type': 'application/json'
      },
      timeout: 15000
    });
    
    const data = response.data;
    
    if (data.success && data.token) {
      api.defaults.headers.common['Authorization'] = `Bearer ${data.token}`;
      localStorage.setItem('token', data.token);
      
      if (data.user) {
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('lastLogin', new Date().toISOString());
      }
      
      return {
        success: true,
        data: data
      };
    } else {
      return {
        success: false,
        message: data.message || 'فشل تسجيل الدخول'
      };
    }
  } catch (error) {
    if (error.code === 'ECONNABORTED') {
      return {
        success: false,
        message: 'انتهت المهلة. تأكد من تشغيل الخادم'
      };
    }
    
    if (error.response?.status === 401) {
      return {
        success: false,
        message: 'البريد الإلكتروني أو كلمة المرور غير صحيحة'
      };
    }
    
    return {
      success: false,
      message: 'تعذر الاتصال بالخادم. تأكد من تشغيل الخادم'
    };
  }
};

api.logout = () => {
  api.setToken(null);
  localStorage.removeItem('user');
  localStorage.removeItem('lastLogin');
  window.location.href = '/login';
};

