import React, { useState, useEffect, useCallback } from 'react';
import { 
  Search, Filter, RefreshCw, Eye, Edit, Trash2,
  CheckCircle, XCircle, Building, User, Calendar,
  DollarSign, Save, ChevronLeft, ChevronRight,
  AlertCircle, Check, X
} from 'lucide-react';
import api from '../../services/api';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== STATES ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  
  // Data States
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  
  // Modal States
  const [showEditModal, setShowEditModal] = useState(false);
  
  // Selection & Editing
  const [selectedRental, setSelectedRental] = useState(null);
  const [editedRental, setEditedRental] = useState(null);
  
  // Filter States
  const [filters, setFilters] = useState({
    status: 'all',
    branch_id: 'all',
    payment_status: 'all'
  });
  
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(15);

  // ==================== LOAD DATA ====================
  const loadAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [branchesRes, rentalsRes] = await Promise.all([
        api.getBranches(),
        api.getRentals({ 
          limit: 1000,
          include_game: true,
          include_branch: true,
          include_employee: true
        })
      ]);
      
      if (branchesRes.success) {
        setBranches(branchesRes.data || []);
      } else {
        throw new Error(branchesRes.message || 'ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹');
      }
      
      if (rentalsRes.success) {
        setRentals(rentalsRes.data || []);
      } else {
        throw new Error(rentalsRes.message || 'ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª');
      }
      
    } catch (error) {
      console.error('Error loading data:', error);
      setError(error.message || 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
      showNotification('error', 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
    } finally {
      setLoading(false);
    }
  };

  // ==================== EFFECTS ====================
  useEffect(() => {
    loadAllData();
  }, []);

  useEffect(() => {
    applyFiltersAndSearch();
  }, [rentals, filters, searchTerm]);

  // ==================== FILTERS & SEARCH ====================
  const applyFiltersAndSearch = () => {
    let result = [...rentals];
    
    // Apply search term
    if (searchTerm.trim()) {
      const term = searchTerm.toLowerCase();
      result = result.filter(rental => {
        return (
          (rental.customer_name && rental.customer_name.toLowerCase().includes(term)) ||
          (rental.rental_number && rental.rental_number.toLowerCase().includes(term)) ||
          (rental.customer_phone && rental.customer_phone.includes(term)) ||
          (rental.game_name && rental.game_name.toLowerCase().includes(term))
        );
      });
    }
    
    // Apply status filter
    if (filters.status !== 'all') {
      result = result.filter(r => r.status === filters.status);
    }
    
    // Apply branch filter
    if (filters.branch_id !== 'all') {
      result = result.filter(r => r.branch_id === filters.branch_id);
    }
    
    // Apply payment status filter
    if (filters.payment_status !== 'all') {
      result = result.filter(r => r.payment_status === filters.payment_status);
    }
    
    setFilteredRentals(result);
    setCurrentPage(1);
  };

  const handleResetFilters = () => {
    setFilters({
      status: 'all',
      branch_id: 'all',
      payment_status: 'all'
    });
    setSearchTerm('');
  };

  // ==================== RENTAL ACTIONS ====================
  const handleEditRental = (rental) => {
    setSelectedRental(rental);
    setEditedRental({ ...rental });
    setShowEditModal(true);
  };

  const handleSaveEdit = async () => {
    if (!editedRental) return;
    
    try {
      setSaving(true);
      setError(null);
      
      const response = await api.updateRental(editedRental.id, editedRental);
      
      if (response.success) {
        showNotification('success', 'âœ… ØªÙ… Ø­ÙØ¸ Ø§Ù„ØªØ¹Ø¯ÙŠÙ„Ø§Øª Ø¨Ù†Ø¬Ø§Ø­');
        setShowEditModal(false);
        loadAllData();
      } else {
        throw new Error(response.message || 'ÙØ´Ù„ ÙÙŠ Ø­ÙØ¸ Ø§Ù„ØªØ¹Ø¯ÙŠÙ„Ø§Øª');
      }
    } catch (error) {
      console.error('Error saving rental:', error);
      setError(error.message);
      showNotification('error', error.message || 'Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø­ÙØ¸ Ø§Ù„ØªØ¹Ø¯ÙŠÙ„Ø§Øª');
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteRental = async (rental) => {
    if (!window.confirm(`Ù‡Ù„ ØªØ±ÙŠØ¯ Ø­Ø°Ù Ø§Ù„ØªØ£Ø¬ÙŠØ± #${rental.rental_number}ØŸ`)) return;
    
    try {
      setError(null);
      
      const response = await api.deleteRental(rental.id);
      
      if (response.success) {
        showNotification('success', 'âœ… ØªÙ… Ø­Ø°Ù Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø¨Ù†Ø¬Ø§Ø­');
        loadAllData();
      } else {
        throw new Error(response.message || 'ÙØ´Ù„ ÙÙŠ Ø­Ø°Ù Ø§Ù„ØªØ£Ø¬ÙŠØ±');
      }
    } catch (error) {
      console.error('Error deleting rental:', error);
      setError(error.message);
      showNotification('error', error.message || 'Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø­Ø°Ù Ø§Ù„ØªØ£Ø¬ÙŠØ±');
    }
  };

  const handleCompleteRental = async (rental) => {
    if (!window.confirm(`Ù‡Ù„ ØªØ±ÙŠØ¯ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ± #${rental.rental_number}ØŸ`)) return;
    
    try {
      setError(null);
      
      const response = await api.completeRental(rental.id, {
        payment_method: rental.payment_method || 'ÙƒØ§Ø´'
      });
      
      if (response.success) {
        showNotification('success', 'âœ… ØªÙ… Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø¨Ù†Ø¬Ø§Ø­');
        loadAllData();
      } else {
        throw new Error(response.message || 'ÙØ´Ù„ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
      }
    } catch (error) {
      console.error('Error completing rental:', error);
      setError(error.message);
      showNotification('error', error.message || 'Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
    }
  };

  const handleCancelRental = async (rental) => {
    if (!window.confirm(`Ù‡Ù„ ØªØ±ÙŠØ¯ Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ± #${rental.rental_number}ØŸ`)) return;
    
    try {
      setError(null);
      
      const response = await api.cancelRental(rental.id);
      
      if (response.success) {
        showNotification('success', 'âœ… ØªÙ… Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø¨Ù†Ø¬Ø§Ø­');
        loadAllData();
      } else {
        throw new Error(response.message || 'ÙØ´Ù„ ÙÙŠ Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
      }
    } catch (error) {
      console.error('Error cancelling rental:', error);
      setError(error.message);
      showNotification('error', error.message || 'Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
    }
  };

  // ==================== UTILITIES ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch {
      return '-';
    }
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      'Ù†Ø´Ø·': { class: 'badge-active', icon: 'ðŸ”¥', text: 'Ù†Ø´Ø·' },
      'Ù…ÙƒØªÙ…Ù„': { class: 'badge-completed', icon: 'âœ…', text: 'Ù…ÙƒØªÙ…Ù„' },
