import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Layout.css';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activePath, setActivePath] = useState('');
  const [isMobile, setIsMobile] = useState(false);
  const sidebarRef = useRef(null);
  const menuToggleRef = useRef(null);

  // ÿ™ÿ≠ÿØŸäÿØ ÿ≠ÿ¨ŸÖ ÿßŸÑÿ¥ÿßÿ¥ÿ© ÿ®ÿ¥ŸÉŸÑ ÿØŸÇŸäŸÇ
  useEffect(() => {
    const checkIsMobile = () => {
      const mobile = window.innerWidth <= 760; // ÿ™ÿ∫ŸäŸäÿ± ŸÖŸÜ 1024 ÿ•ŸÑŸâ 768
      setIsMobile(mobile);
      
      // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿπŸÜÿØ ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ŸÖŸÜ ŸÉŸÖÿ®ŸäŸàÿ™ÿ± ÿ•ŸÑŸâ ŸÖŸàÿ®ÿßŸäŸÑ
      if (mobile && sidebarOpen) {
        setSidebarOpen(false);
      }
      
      // ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿπŸÜÿØ ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ŸÖŸÜ ŸÖŸàÿ®ÿßŸäŸÑ ÿ•ŸÑŸâ ŸÉŸÖÿ®ŸäŸàÿ™ÿ± (ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ŸÖÿ∫ŸÑŸÇÿ©)
      if (!mobile && !sidebarOpen) {
        setSidebarOpen(true);
      }
    };
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿπŸÜÿØ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸàŸÑŸä
    checkIsMobile();
    
    // ÿ™ŸáŸäÿ¶ÿ© ÿ≠ÿßŸÑÿ© ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿ≠ÿ≥ÿ® ŸÜŸàÿπ ÿßŸÑÿ¨Ÿáÿßÿ≤
    const isMobileOnLoad = window.innerWidth <= 768;
    setIsMobile(isMobileOnLoad);
    setSidebarOpen(!isMobileOnLoad); // ŸÖŸÅÿ™Ÿàÿ≠ÿ© ÿπŸÑŸâ ÿßŸÑŸÉŸÖÿ®ŸäŸàÿ™ÿ±ÿå ŸÖÿ∫ŸÑŸÇÿ© ÿπŸÑŸâ ÿßŸÑŸÖŸàÿ®ÿßŸäŸÑ
    
    window.addEventListener('resize', checkIsMobile);
    return () => window.removeEventListener('resize', checkIsMobile);
  }, []);

  // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑŸÜÿ¥ÿ∑
  useEffect(() => {
    setActivePath(location.pathname);
    
    // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿπŸÑŸâ ÿßŸÑŸÖŸàÿ®ÿßŸäŸÑ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÖÿ≥ÿßÿ±
    if (isMobile && sidebarOpen) {
      setSidebarOpen(false);
    }
  }, [location.pathname, isMobile, sidebarOpen]);

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿπŸÜÿØ ÿßŸÑŸÜŸÇÿ± ÿÆÿßÿ±ÿ¨Ÿáÿß (ŸÑŸÑŸÖŸàÿ®ÿßŸäŸÑ ŸÅŸÇÿ∑)
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        isMobile && 
        sidebarOpen &&
        sidebarRef.current &&
        !sidebarRef.current.contains(event.target) &&
        menuToggleRef.current &&
        !menuToggleRef.current.contains(event.target)
      ) {
        setSidebarOpen(false);
      }
    };

    if (isMobile) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('touchstart', handleClickOutside);
    }
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('touchstart', handleClickOutside);
    };
  }, [sidebarOpen, isMobile]);

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿ®ÿßŸÑÿ≤ÿ± Escape (ŸÑŸÉŸÑÿß ÿßŸÑÿ¨Ÿáÿßÿ≤ŸäŸÜ)
  useEffect(() => {
    const handleEscape = (event) => {
      if (event.key === 'Escape' && sidebarOpen) {
        setSidebarOpen(false);
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [sidebarOpen]);

  // ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ŸÖÿ±Ÿäÿ± (ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿπŸÜÿØ ÿßŸÑÿ™ŸÖÿ±Ÿäÿ± ÿπŸÑŸâ ÿßŸÑŸÖŸàÿ®ÿßŸäŸÑ)
  useEffect(() => {
    const handleScroll = () => {
      if (isMobile && sidebarOpen) {
        setSidebarOpen(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [isMobile, sidebarOpen]);

  const handleLogout = () => {
    if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ÿü')) {
      logout();
      navigate('/login');
    }
  };

  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ';
      case 'branch_manager': return 'ŸÖÿØŸäÿ± ŸÅÿ±ÿπ';
      case 'employee': return 'ŸÖŸàÿ∏ŸÅ';
      default: return role;
    }
  };

  const getRoleIcon = (role) => {
    switch(role) {
      case 'admin': return 'üëë';
      case 'branch_manager': return 'üè¢';
      case 'employee': return 'üë§';
      default: return '‚ùì';
    }
  };

  const toggleSidebar = () => {
    setSidebarOpen(prev => !prev);
  };

  const closeSidebar = () => {
    if (isMobile) {
      setSidebarOpen(false);
    }
  };

  const openSidebar = () => {
    if (!isMobile) {
      setSidebarOpen(true);
    }
  };

  const adminMenu = [
    { path: '/admin/dashboard', icon: 'üìä', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ' },
    { path: '/admin/users', icon: 'üë•', label: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ' },
    { path: '/admin/branches', icon: 'üè¨', label: 'ÿßŸÑŸÅÿ±Ÿàÿπ' },
    { path: '/admin/games', icon: 'üéÆ', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®' },
    { path: '/admin/rentals', icon: 'üìã', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™' },
    { path: '/admin/revenue-analysis', icon: 'üí∞', label: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™' },
    { path: '/admin/reports', icon: 'üìà', label: 'ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±' },
    { path: '/admin/settings', icon: '‚öôÔ∏è', label: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™' },
  ];

  const employeeMenu = [
    { path: '/employee/dashboard', icon: 'üìä', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ' },
    { path: '/employee/rentals', icon: 'üìã', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™' },
  ];

  const menuItems = user?.role === 'admin' ? adminMenu : employeeMenu;

  const isActive = (path) => {
    if (path === activePath) return true;
    if (path.includes('/dashboard') && activePath.includes('/dashboard')) return true;
    if (path.includes('/rentals') && activePath.includes('/rentals')) return true;
    return false;
  };

  return (
    <div className="layout">
  {/* ================= HEADER ================= */}
  <header className="header">
    <div className="header-left">
      <button
        ref={menuToggleRef}
        className="menu-toggle"
        onClick={toggleSidebar}
        aria-label={sidebarOpen ? "ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©" : "ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©"}
      >
        <span className="menu-icon">
          {sidebarOpen ? "‚úï" : "‚ò∞"}
        </span>
      </button>

      <div className="header-logo" onClick={() => navigate("/")}>
        <img src="/images/l3bty.png" alt="L3BTY Store Logo" />
        <h1>L3BTY Store</h1>
      </div>
    </div>

    <div className="header-user">
      <div className="user-info">
        <span className="user-role-icon">
          {getRoleIcon(user?.role)}
        </span>

        <div className="user-detailss">
          <span className="user-namee">
            {user?.name || "ŸÖÿ≥ÿ™ÿÆÿØŸÖ"}
          </span>
          <span className="user-rolee">
            {getRoleText(user?.role)}
          </span>
        </div>
      </div>

      <button className="btn-logout" onClick={handleLogout}>
        üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
      </button>
    </div>
  </header>

  {/* Overlay ŸÑŸÑŸÖŸàÿ®ÿßŸäŸÑ */}
  {isMobile && sidebarOpen && (
    <div
      className="sidebar-overlay"
      onClick={closeSidebar}
    />
  )}

  {/* ================= BODY ================= */}
  <div className="layout-body">
    
    {/* -------- SIDEBAR -------- */}
    <aside
      ref={sidebarRef}
      className={`sidebar ${sidebarOpen ? "open" : ""}`}
    >
      <div className="sidebar-header">
        {isMobile && (
          <button
            className="sidebar-close"
            onClick={closeSidebar}
          >
            ‚úï
          </button>
        )}

        <h2>ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</h2>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            onClick={closeSidebar}
            className={`nav-link ${
              isActive(item.path) ? "active" : ""
            }`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span>{item.label}</span>
            {isActive(item.path) && (
              <span className="nav-indicator">‚Üí</span>
            )}
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <span className="branch-name">
          {user?.branch_name || "ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä"}
        </span>

        <div className="branch-status">
          <span className="status-dot active"></span>
          ŸÖÿ™ÿµŸÑ
        </div>
      </div>
    </aside>

    {/* -------- MAIN CONTENT -------- */}
    <main className="main-content">
      {!sidebarOpen && !isMobile && (
        <button
          className="sidebar-toggle-fab"
          onClick={openSidebar}
        >
          ‚ò∞
        </button>
      )}

      <div className="content-container">
        {children}
      </div>
    </main>

  </div>
</div>

  );
};

export default Layout;
