            console.error(`‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ŸÜŸÇŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id}:`, error);
          }
        }
        
        showNotification('info', `ÿ™ŸÖ ŸÜŸÇŸÑ ${expiredRentals.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÜÿ™ŸáŸä ÿ•ŸÑŸâ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©`);
      }
