        if (currentUser) {
          setUserSettings(prev => ({
            ...prev,
            name: currentUser.name || '',
            email: currentUser.email || '',
            phone: currentUser.phone || ''
          }));
        }
      } catch (error) {
        console.error('Error loading user data:', error);
      }
    };

    loadUserData();
  }, []);

  // Ø­ÙØ¸ Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„Ù†Ø¸Ø§Ù…
  const handleSaveSystemSettings = async () => {
    try {
      setSaving(true);
      setErrorMessage('');
      
