import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const BranchesManagement = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [availableGames, setAvailableGames] = useState([]);
  const [branchGames, setBranchGames] = useState([]);
  const [loadingGames, setLoadingGames] = useState(false);
  const [gamesError, setGamesError] = useState('');
  const [showAddExistingModal, setShowAddExistingModal] = useState(false);
  const [existingGames, setExistingGames] = useState([]);
  
  const [newBranch, setNewBranch] = useState({
    name: '',
    location: '',
    city: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©',
    contact_phone: '',
    contact_email: '',
    opening_time: '10:00:00',
    closing_time: '22:00:00'
  });

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹
  useEffect(() => {
    loadBranches();
    loadAllGames(); // ØªØ­Ù…ÙŠÙ„ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø© ÙÙŠ Ø§Ù„Ù†Ø¸Ø§Ù…
  }, []);

  const loadBranches = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await authService.getBranches();
      if (response.success) {
        setBranches(response.data);
      } else {
        setError(response.message || 'ÙØ´Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹');
      }
    } catch (error) {
      setError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…');
      console.error('Error loading branches:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadAllGames = async () => {
    try {
      const response = await authService.getGames();
      if (response.success) {
        setExistingGames(response.data);
      }
    } catch (error) {
      console.error('Error loading all games:', error);
    }
  };

  // âœ… Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ (Ø§Ù„Ù†Ø¸Ø§Ù… Ø§Ù„Ø¬Ø¯ÙŠØ¯)
  const loadBranchGames = async (branchId) => {
    try {
      console.log(`ğŸ”„ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ ${branchId}...`);
      setLoadingGames(true);
      setGamesError('');
      
      // Ø§Ø³ØªØ®Ø¯Ù… Ø§Ù„Ù€ endpoint Ø§Ù„Ø¬Ø¯ÙŠØ¯
      const response = await authService.getBranchGames(branchId);
      
      console.log(`ğŸ“¥ Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹:`, response);
      
      if (response.success) {
        console.log(`âœ… ØªÙ… Ø¬Ù„Ø¨ ${response.data.length} Ù„Ø¹Ø¨Ø© Ù„Ù„ÙØ±Ø¹`);
        setBranchGames(response.data);
      } else {
        console.log(`âŒ ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨: ${response.message}`);
        setGamesError(response.message || 'ÙØ´Ù„ ÙÙŠ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹');
        setBranchGames([]);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹:', error);
      setGamesError('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…: ' + error.message);
      setBranchGames([]);
    } finally {
      setLoadingGames(false);
    }
  };

  // âœ… Ø¥Ø¶Ø§ÙØ© ÙØ±Ø¹ Ø¬Ø¯ÙŠØ¯
  const handleAddBranch = async () => {
    if (!newBranch.name || !newBranch.location) {
      alert('Ø§Ø³Ù… Ø§Ù„ÙØ±Ø¹ ÙˆØ§Ù„Ù…ÙˆÙ‚Ø¹ Ù…Ø·Ù„ÙˆØ¨Ø§Ù†');
      return;
    }

    try {
      const response = await authService.createBranch(newBranch);
      if (response.success) {
        alert('âœ… ØªÙ… Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„ÙØ±Ø¹ Ø¨Ù†Ø¬Ø§Ø­');
        setShowAddModal(false);
        setNewBranch({
          name: '',
          location: '',
          city: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©',
          contact_phone: '',
          contact_email: '',
          opening_time: '10:00:00',
          closing_time: '22:00:00'
        });
        loadBranches();
      } else {
        alert(response.message || 'ÙØ´Ù„ ÙÙŠ Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„ÙØ±Ø¹');
      }
    } catch (error) {
      alert('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…');
    }
  };

  // âœ… Ø¥Ø¶Ø§ÙØ© Ø¯Ø§Ù„Ø© handleDeleteBranch
const handleDeleteBranch = async (id) => {
  const choice = window.confirm(
    'Ø§Ø®ØªØ± Ù†ÙˆØ¹ Ø§Ù„Ø­Ø°Ù:\n\n' +
    '"Ù…ÙˆØ§ÙÙ‚" Ù„Ù„Ø­Ø°Ù Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠ (ÙŠØªÙ… Ø­Ø°Ù Ø§Ù„ÙØ±Ø¹ Ù…Ù† Ù‚Ø§Ø¹Ø¯Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù†Ù‡Ø§Ø¦ÙŠØ§Ù‹)\n' +
    '"Ø¥Ù„ØºØ§Ø¡" Ù„Ù„ØªØ¹Ø·ÙŠÙ„ ÙÙ‚Ø· (ÙŠØ¸Ù„ Ø§Ù„ÙØ±Ø¹ ÙÙŠ Ù‚Ø§Ø¹Ø¯Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙˆÙ„ÙƒÙ†Ù‡ ØºÙŠØ± Ù†Ø´Ø·)'
  );

  try {
    if (choice) {
      // Ø§Ù„Ø­Ø°Ù Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠ
      if (!window.confirm('âš ï¸ ØªØ­Ø°ÙŠØ±: Ù‡Ø°Ø§ Ø§Ù„Ø¥Ø¬Ø±Ø§Ø¡ Ù„Ø§ ÙŠÙ…ÙƒÙ† Ø§Ù„ØªØ±Ø§Ø¬Ø¹ Ø¹Ù†Ù‡!\nÙ‡Ù„ ØªØ±ÙŠØ¯ Ø­Ø°Ù Ø§Ù„ÙØ±Ø¹ Ù†Ù‡Ø§Ø¦ÙŠØ§Ù‹ Ù…Ù† Ù‚Ø§Ø¹Ø¯Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§ØªØŸ')) {
        return;
      }
      
      const response = await authService.deleteBranch(id);
      
      if (response.success) {
        alert('âœ… ØªÙ… Ø­Ø°Ù Ø§Ù„ÙØ±Ø¹ Ù†Ù‡Ø§Ø¦ÙŠØ§Ù‹ Ù…Ù† Ù‚Ø§Ø¹Ø¯Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
        loadBranches();
      } else {
        // Ø¥Ø°Ø§ ÙØ´Ù„ Ø§Ù„Ø­Ø°Ù Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠØŒ Ø­Ø§ÙˆÙ„ Ø§Ù„ØªØ¹Ø·ÙŠÙ„
