// api.js - ÿßŸÑŸÜÿ≥ÿÆÿ© ÿßŸÑŸÖÿµÿ≠ÿ≠ÿ©
import axios from 'axios';

// üî• ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑÿ®Ÿàÿ±ÿ™ ÿµÿ≠Ÿäÿ≠
const API_BASE_URL = 'http://localhost:5000/api';

console.log('üåê API Base URL:', API_BASE_URL);

// üî• ÿ•ŸÜÿ¥ÿßÿ° axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000, // 10 ÿ´ÿßŸÜŸäÿ©
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

// üî• Request Interceptor ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// üî• Response Interceptor ŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ£ÿÆÿ∑ÿßÿ°
api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ API Response:', {
      url: response.config.url,
      status: response.status,
      data: response.data
    });
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ±ÿØ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ data.success
    if (response.data && typeof response.data.success !== 'undefined') {
      return response.data;
    }
    
    // ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸÅŸäŸá successÿå ŸÜÿ±ÿ¨ÿπŸá ŸÉŸÖÿß ŸáŸà
    return {
      success: true,
      data: response.data
    };
  },
  (error) => {
    console.error('‚ùå API Error:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.message,
      response: error.response?.data
    });
    
    // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ£ÿÆÿ∑ÿßÿ° ÿßŸÑÿßÿ™ÿµÿßŸÑ
    if (!error.response) {
      console.error('üõë No response from server - Check if backend is running');
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ±.',
        error: 'CONNECTION_REFUSED'
      };
    }
    
    // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ£ÿÆÿ∑ÿßÿ° HTTP
    if (error.response.status === 401) {
      // ŸÖÿ≥ÿ≠ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    
    return {
      success: false,
      message: error.response.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ',
      status: error.response.status
    };
  }
);

// üî• ÿØŸàÿßŸÑ API ŸÖÿ≥ÿßÿπÿØÿ©
export const testConnection = async () => {
  try {
    const response = await api.get('/health');
    return response;
  } catch (error) {
    return {
      success: false,
      message: 'ŸÅÿ¥ŸÑ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ±',
      error: error.message
    };
  }
};

export const login = async (email, password) => {
  try {
    // ŸÖÿ≠ÿßŸàŸÑÿ© 1: ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿπÿßÿØŸä
    try {
      const response = await api.post('/auth/login', { email, password });
      return response;
    } catch (error) {
      // ŸÖÿ≠ÿßŸàŸÑÿ© 2: ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿ™ÿ¨ÿ±Ÿäÿ®Ÿä ÿ•ÿ∞ÿß ŸÅÿ¥ŸÑ ÿßŸÑÿ£ŸàŸÑ
      console.log('üîÑ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 2: ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿ™ÿ¨ÿ±Ÿäÿ®Ÿä');
      const response = await api.post('/auth/test-login', { email, password });
      return response;
    }
  } catch (error) {
    return {
      success: false,
      message: error.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
    };
  }
};

export default api;
